#include <bits/stdc++.h>
using namespace std;

vector<vector<pair<int, int>>> adj;
vector<bool> visitedEdge;
vector<int> deg;
vector<int> path;
int m;

void hierholzer(int u) {
    while (!adj[u].empty()) {
        auto [v, idx] = adj[u].back();
        adj[u].pop_back();
        if (visitedEdge[idx]) continue;
        visitedEdge[idx] = true;
        hierholzer(v);
        path.push_back(idx);
    }
}

vector<int> findEulerianTrail(int n) {
    int start = 1;
    int cnt = 0;
    for (int i = 1; i <= n; ++i) {
        if (deg[i] % 2) {
            ++cnt;
            start = i;
        }
    }
    if (cnt != 0 && cnt != 2) return {};

    visitedEdge.assign(m + 1, false);
    path.clear();
    hierholzer(start);
    reverse(path.begin(), path.end());
    if (path.size() != m) return {};
    return path;
}

vector<int> reconstructPath(const vector<int>& edgeOrder, const vector<pair<int, int>>& edges) {
    vector<unordered_multiset<int>> g(edges.size() + 1);
    for (int i = 0; i < edges.size(); ++i) {
        int u = edges[i].first, v = edges[i].second;
        g[u].insert(i);
        g[v].insert(i);
    }

    vector<int> res;
    if (edgeOrder.empty()) return res;

    res.push_back(edges[edgeOrder[0]].first);
    res.push_back(edges[edgeOrder[0]].second);
    g[res[0]].erase(edgeOrder[0]);
    g[res[1]].erase(edgeOrder[0]);

    for (size_t i = 1; i < edgeOrder.size(); ++i) {
        int idx = edgeOrder[i];
        int last = res.back();
        if (g[last].count(idx)) {
            res.push_back(edges[idx].first + edges[idx].second - last);
        } else {
            return {}; 
        }
        g[res[res.size()-2]].erase(idx);
        g[res.back()].erase(idx);
    }
    return res;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n >> m;
    vector<pair<int, int>> edges(m + 1);
    adj.resize(n + 1);
    deg.assign(n + 1, 0);
    for (int i = 1; i <= m; ++i) {
        int u, v;
        cin >> u >> v;
        edges[i] = {u, v};
        adj[u].emplace_back(v, i);
        adj[v].emplace_back(u, i);
        deg[u]++;
        deg[v]++;
    }

    vector<int> trail = findEulerianTrail(n);
    if (!trail.empty()) {
        vector<int> path = reconstructPath(trail, edges);
        if (!path.empty()) {
            cout << path.size() << "\n";
            for (int u : path) {
                cout << u << " ";
            }
            cout << "\n";
            return 0;
        }
    }

    vector<int> odd;
    for (int i = 1; i <= n; ++i) {
        if (deg[i] % 2) {
            odd.push_back(i);
        }
    }
    if (odd.size() % 2 != 0) {
        cout << "0\n";
        return 0;
    }

    vector<int> parent(n + 1, -1);
    queue<int> q;
    q.push(odd[0]);
    parent[odd[0]] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (auto [v, idx] : adj[u]) {
            if (parent[v] == -1) {
                parent[v] = idx;
                q.push(v);
            }
        }
    }
    if (parent[odd[1]] == -1) {
        cout << "0\n";
        return 0;
    }

    vector<bool> inS(m + 1, false);
    int v = odd[1];
    while (v != odd[0]) {
        int idx = parent[v];
        inS[idx] = true;
        auto [u1, u2] = edges[idx];
        v = (u1 == v) ? u2 : u1;
    }

    vector<vector<pair<int, int>>> adj2(n + 1);
    int newM = 0;
    vector<pair<int, int>> newEdges;
    for (int i = 1; i <= m; ++i) {
        if (!inS[i]) {
            auto [u, v] = edges[i];
            adj2[u].emplace_back(v, newM);
            adj2[v].emplace_back(u, newM);
            newEdges.push_back({u, v});
            newM++;
        }
    }

    vector<int> deg2(n + 1, 0);
    for (int i = 1; i <= m; ++i) {
        if (!inS[i]) {
            deg2[edges[i].first]++;
            deg2[edges[i].second]++;
        }
    }

    int start = -1, end = -1;
    for (int i = 1; i <= n; ++i) {
        if (deg2[i] % 2) {
            if (start == -1) start = i;
            else end = i;
        }
    }
    if (start == -1) start = 1;

    visitedEdge.assign(newM, false);
    vector<int> trail2;
    function<void(int)> hierholzer2 = [&](int u) {
        while (!adj2[u].empty()) {
            auto [v, idx] = adj2[u].back();
            adj2[u].pop_back();
            if (visitedEdge[idx]) continue;
            visitedEdge[idx] = true;
            hierholzer2(v);
            trail2.push_back(idx);
        }
    };
    hierholzer2(start);
    reverse(trail2.begin(), trail2.end());

    if (trail2.size() != newM) {
        cout << "0\n";
        return 0;
    }

    vector<int> path1 = reconstructPath(trail2, newEdges);
    if (path1.empty()) {
        cout << "0\n";
        return 0;
    }

    vector<int> sEdges;
    for (int i = 1; i <= m; ++i) {
        if (inS[i]) sEdges.push_back(i);
    }

    vector<int> path2;
    if (!sEdges.empty()) {
        int u = path1.empty() ? start : path1.back();
        vector<int> temp;
        for (int idx : sEdges) {
            temp.push_back(idx);
            temp.push_back(idx);
        }
        vector<pair<int, int>> tempEdges;
        for (int idx : temp) {
            tempEdges.push_back(edges[idx]);
        }
        path2 = reconstructPath(temp, tempEdges);
        if (path2.empty()) {
            cout << "0\n";
            return 0;
        }
        path2.erase(path2.begin());
    }

    vector<int> result;
    result.insert(result.end(), path1.begin(), path1.end());
    result.push_back(-1);
    result.insert(result.end(), path2.begin(), path2.end());

    cout << result.size() << "\n";
    for (int u : result) {
        cout << u << " ";
    }
    cout << "\n";
    return 0;
}

