#include <bits/stdc++.h>
using namespace std;

const int MOD = 1e9 + 7;
const int MAXN = 2e5 + 5;

vector<int> adj[MAXN];
long long dp0[MAXN];
long long ans[MAXN];
int n;

long long pow_mod(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b % 2) res = res * a % MOD;
        a = a * a % MOD;
        b /= 2;
    }
    return res;
}

long long mod_inverse(long long a) {
    return pow_mod(a, MOD - 2);
}

void dfs(int x, int parent) {
    dp0[x] = 1;
    for (int y : adj[x]) {
        if (y == parent) continue;
        dfs(y, x);
        dp0[x] = dp0[x] * (1 + dp0[y]) % MOD;
    }
    ans[x] = dp0[x];
}

void dfs_reroot(int x, int parent) {
    for (int y : adj[x]) {
        if (y == parent) continue;
        long long denominator = (1 + dp0[y]) % MOD;
        long long inv_denominator = mod_inverse(denominator);
        long long x_part = ans[x] * inv_denominator % MOD;
        x_part = (1 + x_part) % MOD;
        ans[y] = ans[y] * x_part % MOD;
        dfs_reroot(y, x);
    }
}

