                    if (x_known) x--;
                }
            }
        }

        cout << "! ";
        for (int i = 1; i <= n; ++i)
            cout << a[i] << " ";
        cout << endl;
    }

    return 0;
}

#include<bits/stdc++.h>
using namespace std;

typedef long long ll;

int solve() {
    int n, t, a, b;
    cin >> n >> t >> a >> b;

    vector<int> type(n);
    for (int i = 0; i < n; ++i) {
        cin >> type[i];
    }

    vector<int> time(n);
    for (int i = 0; i < n; ++i) {
        cin >> time[i];
    }

    vector<pair<int, int>> problems(n);
    for (int i = 0; i < n; ++i) {
        problems[i] = {time[i], type[i]};
    }

    sort(problems.begin(), problems.end());

    int ans = 0;

    for (int l = 0; l <= n; ++l) {
        int cur_time = 0;
        int solved = 0;
        bool possible = true;

        for (int i = 0; i < l; ++i) {
            if (problems[i].second == 0) {
                cur_time += a;
            } else {
                cur_time += b;
            }
        }

        if (cur_time > t) continue;

        for (int i = 0; i < l; ++i) {
            if (problems[i].first > (ll)t) {
                possible = false;
                break;
            }
        }

        if (!possible) continue;

        int mandatory_solved = l;
        int remaining_time = t - cur_time;

        vector<pair<int, int>> remaining_problems;
        for (int i = 0; i < n; ++i) {
            if (i >= l) {
                remaining_problems.emplace_back(problems[i].second == 0 ? a : b, problems[i].first);
            }
        }

        sort(remaining_problems.begin(), remaining_problems.end());

        for (int i = 0; i < remaining_problems.size(); ++i) {
            if (remaining_problems[i].first <= remaining_time) {
                remaining_time -= remaining_problems[i].first;
                solved++;
            }
        }

        ans = max(ans, mandatory_solved + solved);
    }

    int initial_time = 0;
    int initial_solved = 0;
    bool initial_possible = true;

    for (int i = 0; i < n; ++i) {
        if (problems[i].second == 0) {
            initial_time += a;
        } else {
            initial_time += b;
        }
    }
    if(initial_time <= t)
        ans = max(ans, n);

    return ans;
}

int main() {
    int num_test_cases;
    cin >> num_test_cases;

    while (num_test_cases--) {
        cout << solve() << '\n';
    }

    return 0;
}

