const int MOD = 1e9 + 7;
const int MOD_LEN = MOD - 1;

long long pow10_mod(int exponent) {
    if (exponent == 0) return 1LL;
    long long result = 1;
    long long base = 10;
    while (exponent > 0) {
        if (exponent % 2 == 1) {
            result = (result * base) % MOD;
        }
        base = (base * base) % MOD;
        exponent /= 2;
    }
    return result;
}

int main() {
