    for (int d = 0; d < 10; ++d) {
        val[d] = d;
        len[d] = 1;
    }

    for (auto it = queries.rbegin(); it != queries.rend(); ++it) {
        string q = *it;
        char di_char = q[0];
        int di = di_char - '0';
