        int new_len = 0;

        for (char c : ti) {
            int curr_d = c - '0';
            long long p = pow10_mod(len[curr_d]);
            new_val = (new_val * p + val[curr_d]) % MOD;
            new_len = (new_len + len[curr_d]) % MOD_LEN;
        }

        val[di] = new_val;
        len[di] = new_len;
    }

    long long result = 0;
    for (char c : s) {
        int d = c - '0';
        long long p = pow10_mod(len[d]);
