#include <bits/stdc++.h>
using namespace std;

struct State {
    bool is_a;
    int a, b;
    int last_time;
    int flips;
    int interval_idx;
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, k;
    cin >> n >> k;
    vector<pair<int, int>> intervals(k);
    for (auto& [l, r] : intervals) {
        cin >> l >> r;
    }

    // Check for single flip
    bool has_single = false;
    for (auto& [l, r] : intervals) {
        if (l <= n && n <= r) {
            has_single = true;
            break;
        }
    }
    if (has_single) {
        cout << "Full\n1\n";
        return 0;
    }

    // Check for two flips
    bool has_two = false;
    for (int i = 0; i < k; ++i) {
        auto [l1, r1] = intervals[i];
        for (int j = i; j < k; ++j) {
            auto [l2, r2] = intervals[j];
            int low = max(l1, l2 - n);
            int high = min(r1, r2 - n);
            if (low <= high) {
                has_two = true;
                break;
            }
        }
        if (has_two) break;
    }
    if (has_two) {
        cout << "Full\n2\n";
        return 0;
    }

    // BFS approach
    queue<State> q;
    q.push({true, 0, 0, 0, 0, 0});
    vector<vector<map<int, int>>> visited(2, vector<map<int, int>>(k + 1));

    int best = INT_MAX;

    while (!q.empty()) {
        auto state = q.front();
        q.pop();

        if (state.interval_idx == k) {
            int remaining = 2 * n - state.last_time;
            int a_final = state.a + (state.is_a ? remaining : 0);
            int b_final = state.b + (state.is_a ? 0 : remaining);
            if (a_final == n && b_final == n) {
                best = min(best, state.flips);
            }
            continue;
        }

        if (state.interval_idx >= k) continue;

        auto [l, r] = intervals[state.interval_idx];
        if (state.last_time > r) {
            // Move to next interval
            State next = state;
            next.interval_idx += 1;
            if (!visited[next.is_a][next.interval_idx].count(next.last_time) || visited[next.is_a][next.interval_idx][next.last_time] > next.flips) {
                visited[next.is_a][next.interval_idx][next.last_time] = next.flips;
                q.push(next);
            }
            continue;
        }

        int start = max(state.last_time, l);
        int end = r;

        // Option 1: Do not flip in this interval
        State next_no_flip = state;
        next_no_flip.interval_idx += 1;
        int delta = end - state.last_time;
        if (next_no_flip.is_a) {
            next_no_flip.a += delta;
        } else {
            next_no_flip.b += delta;
        }
        next_no_flip.last_time = end;
        if (next_no_flip.a <= n && next_no_flip.b <= n) {
            auto& vis = visited[next_no_flip.is_a][next_no_flip.interval_idx];
            if (!vis.count(end) || vis[end] > next_no_flip.flips) {
                vis[end] = next_no_flip.flips;
                q.push(next_no_flip);
            }
        }

        // Option 2: Flip at start (earliest)
        State flip_start = state;
        int delta_start = start - state.last_time;
        if (flip_start.is_a) {
            flip_start.a += delta_start;
        } else {
            flip_start.b += delta_start;
        }
        flip_start.is_a = !flip_start.is_a;
        flip_start.last_time = start;
        flip_start.flips += 1;
        flip_start.interval_idx += 1;
        if (flip_start.a <= n && flip_start.b <= n) {
            auto& vis = visited[flip_start.is_a][flip_start.interval_idx];
            if (!vis.count(start) || vis[start] > flip_start.flips) {
                vis[start] = flip_start.flips;
                q.push(flip_start);
            }
        }

        // Option 3: Flip at end (latest)
        State flip_end = state;
        int delta_end = end - state.last_time;
        if (flip_end.is_a) {
            flip_end.a += delta_end;
        } else {
            flip_end.b += delta_end;
        }
        flip_end.is_a = !flip_end.is_a;
        flip_end.last_time = end;
        flip_end.flips += 1;
        flip_end.interval_idx += 1;
        if (flip_end.a <= n && flip_end.b <= n) {
            auto& vis = visited[flip_end.is_a][flip_end.interval_idx];
            if (!vis.count(end) || vis[end] > flip_end.flips) {
                vis[end] = flip_end.flips;
                q.push(flip_end);
            }
        }
    }

    if (best != INT_MAX) {
        cout << "Full\n" << best << "\n";
    } else {
        cout << "Hungry\n";
    }
    return 0;
}

