const int MOD = 1e9+7;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        string s;
        cin >> s;
        int n = s.size();
        vector<int> balance(n + 1, 0);
        for (int i = 0; i < n; ++i) {
            balance[i+1] = balance[i] + (s[i] == '1' ? 1 : -1);
        }

        unordered_map<int, vector<int>> mp;
        for (int i = 0; i <= n; ++i) {
            mp[balance[i]].push_back(i);
        }

        long long ans = 0;
        for (auto& [b, v] : mp) {
            int m = v.size();
            if (m < 2) continue;

            vector<long long> suffix_sum(m+1, 0);
            for (int i = m-1; i >= 0; --i) {
                suffix_sum[i] = suffix_sum[i+1] + v[i];
            }

            long long t1 = 0, t2 = 0;
            for (int p = 0; p < m-1; ++p) {
                int cnt = (m-1) - p;
                t1 += (v[p] + 1LL) * cnt;
                t2 += (v[p] + 1LL) * suffix_sum[p+1];
            }

            long long term1_mod = ( ( (n + 1LL) % MOD ) * (t1 % MOD) ) % MOD;
            long long term2_mod = t2 % MOD;
            long long contrib = (term1_mod - term2_mod + MOD) % MOD;
            ans = (ans + contrib) % MOD;
        }

        cout << ans << '\n';
    }

    return 0;
}

