#include <bits/stdc++.h>
#include "biscuits.h"
#include <vector>
#include <numeric>
#include <algorithm>
using namespace std;

long long gcd(long long a, long long b) {
    while (b) {
        a %= b;
        swap(a, b);
    }
    return a;
}

long long count_tastiness(long long x, vector<long long> a) {
    int k = a.size();
    vector<long long> dp(x, 0);
    dp[0] = 1;

    for (int i = 0; i < k; ++i) {
        long long pow2 = 1LL << i;
        long long mod_val = pow2 % x;
        if (mod_val == 0) {
            long long cnt = a[i] + 1;
            for (int r = 0; r < x; ++r)
                dp[r] *= cnt;
            continue;
        }

        long long g = gcd(mod_val, x);
        long long m = mod_val / g;
        long long n = x / g;

        long long inv_m = -1;
        for (long long j = 0; j < n; ++j) {
            if ((m * j) % n == 1 % n) {
                inv_m = j;
                break;
            }
        }

        vector<long long> new_dp(x, 0);
        if (inv_m == -1) {
            for (long long delta = 0; delta < x; delta += g) {
                long long k_val = delta / g;
                long long s0 = (k_val * inv_m) % n;
                if (s0 > a[i]) continue;
                long long count = (a[i] - s0) / n + 1;
                for (int r_prev = 0; r_prev < x; ++r_prev) {
                    if (dp[r_prev] == 0) continue;
                    int r_new = (r_prev + delta) % x;
                    new_dp[r_new] += dp[r_prev] * count;
                }
            }
        } else {
            vector<long long> delta_count(x, 0);
            for (long long delta = 0; delta < x; ++delta) {
                if (delta % g != 0) {
                    delta_count[delta] = 0;
                    continue;
                }
                long long k_val = delta / g;
                long long s0 = (k_val * inv_m) % n;
                if (s0 > a[i]) {
                    delta_count[delta] = 0;
                    continue;
                }
                long long max_s = s0 + ((a[i] - s0) / n) * n;
                if (max_s > a[i]) max_s -= n;
                long long count = (max_s >= s0) ? ((max_s - s0) / n + 1) : 0;
                delta_count[delta] = count;
            }
            for (int r_prev = 0; r_prev < x; ++r_prev) {
                if (dp[r_prev] == 0) continue;
                for (long long delta = 0; delta < x; ++delta) {
                    if (delta_count[delta] == 0) continue;
                    int r_new = (r_prev + delta) % x;
                    new_dp[r_new] += dp[r_prev] * delta_count[delta];
                }
            }
        }

        for (int r = 0; r < x; ++r) {
            dp[r] = new_dp[r];
        }
    }

    return dp[0];
}

