const int MOD = 998244353;

int n;
vector<string> adj;

vector<int> kosaraju(const vector<vector<int>>& g) {
    vector<int> order;
    vector<bool> visited(n, false);
    function<void(int)> dfs = [&](int u) {
        visited[u] = true;
        for (int v : g[u]) if (!visited[v]) dfs(v);
        order.push_back(u);
    };
    for (int i = 0; i < n; ++i) if (!visited[i]) dfs(i);

    vector<vector<int>> r(n);
    for (int u = 0; u < n; ++u)
        for (int v : g[u])
            r[v].push_back(u);

    fill(visited.begin(), visited.end(), false);
    vector<int> component(n, -1);
    int current = 0;
    reverse(order.begin(), order.end());
    for (int u : order) {
        if (!visited[u]) {
            stack<int> s;
            s.push(u);
            visited[u] = true;
            component[u] = current;
            while (!s.empty()) {
                int v = s.top(); s.pop();
                for (int w : r[v]) {
                    if (!visited[w]) {
                        visited[w] = true;
                        component[w] = current;
                        s.push(w);
                    }
                }
            }
            ++current;
        }
    }
    return component;
}

bool is_strongly_connected(const vector<vector<int>>& g) {
    auto component = kosaraju(g);
    return (count(component.begin(), component.end(), component[0]) == n);
}

