        for (int j = 0; j < n; ++j)
            if (adj[i][j] == '1')
                original_graph[i].push_back(j);

    if (is_strongly_connected(original_graph)) {
        cout << "0 1\n";
        return 0;
    }

    int best = -1;
    long long cnt = 0;

    // Check k=1
    for (int v = 0; v < n; ++v) {
        vector<vector<int>> modified = original_graph;
        for (int u = 0; u < n; ++u) {
            if (u == v) continue;
            bool has_edge = adj[u][v] == '1';
            if (has_edge) {
                modified[u].erase(find(modified[u].begin(), modified[u].end(), v));
