        }
        if (is_strongly_connected(modified)) {
            if (best == -1 || 1 < best) {
                best = 1;
                cnt = 0;
            }
            if (best == 1)
                ++cnt;
        }
    }
    if (best != -1) {
        cout << "1 " << cnt << '\n';
        return 0;
    }

    // Check k=2
    for (int v1 = 0; v1 < n; ++v1) {
        for (int v2 = v1 + 1; v2 < n; ++v2) {
            vector<vector<int>> modified = original_graph;
            for (int u = 0; u < n; ++u) {
                if (u != v1) {
                    bool has_edge = adj[u][v1] == '1';
                    if (has_edge) {
                        auto it = find(modified[u].begin(), modified[u].end(), v1);
                        if (it != modified[u].end()) modified[u].erase(it);
                        modified[v1].push_back(u);
                    } else {
                        auto it = find(modified[v1].begin(), modified[v1].end(), u);
                        if (it != modified[v1].end()) modified[v1].erase(it);
                        modified[u].push_back(v1);
                    }
                }
                if (u != v2) {
                    bool has_edge = adj[u][v2] == '1';
                    if (has_edge) {
                        auto it = find(modified[u].begin(), modified[u].end(), v2);
                        if (it != modified[u].end()) modified[u].erase(it);
                        modified[v2].push_back(u);
                    } else {
                        auto it = find(modified[v2].begin(), modified[v2].end(), u);
                        if (it != modified[v2].end()) modified[v2].erase(it);
                        modified[u].push_back(v2);
                    }
                }
            }
            if (is_strongly_connected(modified)) {
                if (best == -1 || 2 < best) {
                    best = 2;
                    cnt = 0;
                }
                if (best == 2)
                    cnt += 2;
            }
        }
    }

    if (best != -1) {
        cnt %= MOD;
        cout << best << ' ' << cnt << '\n';
        return 0;
    }

    cout << "-1\n";
    return 0;
}

#include <bits/stdc++.h>
