using ll = long long;

void solve() {
    int n;
    cin >> n;
    vector<ll> b(n);
    ll sum_b = 0;
    for (auto &x : b) {
        cin >> x;
        sum_b += x;
    }

    if (n == 1) {
        if (sum_b == 1) {
            cout << "YES\n1\n";
        } else {
            cout << "NO\n";
        }
        return;
    }

    ll denominator = (ll)n * (n + 1) / 2;
    if (sum_b % denominator != 0) {
        cout << "NO\n";
        return;
    }
    ll S = sum_b / denominator;

    vector<ll> a(n);
    bool possible = true;
    for (int i = 0; i < n; ++i) {
        int prev = (i - 1 + n) % n;
        ll delta = b[i] - b[prev];
        ll numerator = S - delta;
        if (numerator % n != 0) {
            possible = false;
            break;
        }
        a[i] = numerator / n;
        if (a[i] <= 0) {
            possible = false;
            break;
        }
    }

    if (possible) {
        cout << "YES\n";
        for (ll x : a) {
            cout << x << ' ';
        }
        cout << '\n';
    } else {
        cout << "NO\n";
    }
}

