}

#include <bits/stdc++.h>
using namespace std;

struct DSU {
    vector<int> parent;
    DSU(int n) {
        parent.resize(n + 1);
        iota(parent.begin(), parent.end(), 0);
    }
    int find(int x) {
        if (parent[x] != x)
            parent[x] = find(parent[x]);
        return parent[x];
    }
    void unite(int x, int y) {
        x = find(x);
        y = find(y);
        if (x != y) {
            if (x < y) parent[y] = x;
            else parent[x] = y;
        }
    }
};

vector<bool> sieve(int n) {
    vector<bool> is_prime(n + 1, true);
    is_prime[0] = is_prime[1] = false;
    for (int i = 2; i * i <= n; ++i) {
        if (is_prime[i])
            for (int j = i * i; j <= n; j += i)
                is_prime[j] = false;
    }
    return is_prime;
}

int main() {
    string s;
    cin >> s;
    int n = s.size();

    vector<bool> is_prime = sieve(n);
    DSU dsu(n);

    for (int p = 2; p <= n; ++p) {
        if (is_prime[p])
            for (int i = p; i <= n; i += p)
                dsu.unite(p, i);
    }

    unordered_map<int, int> root_sizes;
    for (int i = 1; i <= n; ++i)
        root_sizes[dsu.find(i)]++;

    vector<pair<int, int>> groups;
    for (const auto& [root, size] : root_sizes)
        groups.emplace_back(size, root);
    sort(groups.begin(), groups.end(), greater<pair<int, int>>());

    vector<int> freq(26);
    for (char c : s) freq[c - 'a']++;

    priority_queue<int> pq;
    for (int cnt : freq)
        if (cnt > 0) pq.push(cnt);

    bool possible = true;
    for (const auto& [size, _] : groups) {
        if (pq.empty() || pq.top() < size) {
            possible = false;
            break;
        }
        int cnt = pq.top() - size;
        pq.pop();
        if (cnt > 0) pq.push(cnt);
    }

    if (!possible) {
        cout << "NO\n";
        return 0;
    }

    vector<pair<int, char>> chars;
    for (int i = 0; i < 26; ++i)
        if (freq[i] > 0) chars.emplace_back(freq[i], 'a' + i);
    sort(chars.begin(), chars.end(), greater<pair<int, char>>());

    unordered_map<int, char> root_char;
    for (const auto& [size, root] : groups) {
        for (auto it = chars.begin(); it != chars.end(); ++it) {
            if (it->first >= size) {
                char c = it->second;
                int new_cnt = it->first - size;
                root_char[root] = c;
                chars.erase(it);
                if (new_cnt > 0) {
                    auto pos = lower_bound(chars.begin(), chars.end(), make_pair(new_cnt, c), greater<pair<int, char>>());
                    chars.insert(pos, {new_cnt, c});
                }
                break;
            }
        }
    }

    string result(n, ' ');
    for (int i = 1; i <= n; ++i)
        result[i-1] = root_char[dsu.find(i)];

    cout << "YES\n" << result << endl;
    return 0;
}

#include <bits/stdc++.h>
using namespace std;

void solve() {
    int n, d12, d23, d31;
    cin >> n >> d12 >> d23 >> d31;

    int sum = d12 + d23 + d31;
    if (sum % 2 != 0) {
        cout << "NO\n";
        return;
    }

    int x = sum / 2;
    int a = x - d23;
    int b = x - d31;
    int c = x - d12;

    if (a < 0 || b < 0 || c < 0) {
        cout << "NO\n";
        return;
    }

    int root = 1;
    if (a + b + c + 1 > n) {
        cout << "NO\n";
        return;
    }

    cout << "YES\n";
    int next_node = 4;
    if (a + b + c == 0) {
        root = 1;
    } else {
        root = 4;
        next_node = 5;
    }

    vector<pair<int, int>> edges;
    int last1 = 1, last2 = 2, last3 = 3;

    if (a > 0) {
        edges.emplace_back(root, 1);
        for (int i = 1; i < a; i++) {
            edges.emplace_back(1, next_node);
            last1 = next_node;
            next_node++;
        }
        last1 = 1;
    } else {
        last1 = root;
    }

    if (b > 0) {
        edges.emplace_back(root, 2);
        for (int i = 1; i < b; i++) {
            edges.emplace_back(2, next_node);
            last2 = next_node;
            next_node++;
        }
        last2 = 2;
    } else {
        last2 = root;
    }

    if (c > 0) {
        edges.emplace_back(root, 3);
        for (int i = 1; i < c; i++) {
            edges.emplace_back(3, next_node);
            last3 = next_node;
            next_node++;
        }
        last3 = 3;
    } else {
        last3 = root;
    }

    edges.emplace_back(last1, last2);
    edges.emplace_back(last2, last3);

    while (next_node <= n) {
        edges.emplace_back(root, next_node);
        next_node++;
    }

    for (auto e : edges) {
        cout << e.first << " " << e.second << '\n';
    }
}

int main() {
    int t;
    cin >> t;
    while (t--) {
        solve();
    }
    return 0;
}

