using namespace std;

long long power_of_10(int exponent) {
    long long res = 1;
    for (int i = 0; i < exponent; ++i) {
        res *= 10;
    }
    return res;
}

long long f(long long X) {
    if (X < 1) return 0;
    string s = to_string(X);
    int n = s.size();
    long long res = 0;
    for (int k = 1; k <= n; ++k) {
        for (int d = 1; d <= 9; ++d) {
            if (k < n) {
                if (k == 1) {
                    res += 1;
                } else {
                    res += power_of_10(k - 2);
                }
            } else {
                int first_digit = s[0] - '0';
                if (d < first_digit) {
                    if (k == 1) {
                        res += 1;
                    } else {
                        res += power_of_10(k - 2);
                    }
                } else if (d == first_digit) {
                    long long pow10 = power_of_10(k - 1);
                    long long part1 = d * pow10;
                    long long part2 = d;
                    long long total = part1 + part2;
                    if (total > X) {
                        continue;
                    }
                    long long R = X - total;
                    long long m_max = R / 10;
                    long long max_m_allowed = power_of_10(k - 2) - 1;
                    long long possible_m_max = min(m_max, max_m_allowed);
                    if (possible_m_max < 0) {
                        continue;
                    }
                    res += possible_m_max + 1;
                }
            }
        }
    }
    return res;
}

int main() {
    long long l, r;
    cin >> l >> r;
    long long ans = f(r) - (l > 1 ? f(l - 1) : 0);
    cout << ans << endl;
    return 0;
}

