#include <bits/stdc++.h>
#include "closing.h"

#include <vector>
#include <queue>
#include <algorithm>
#include <unordered_map>
#include <unordered_set>
#include <climits>
#include <cstdint>

using namespace std;

struct Edge {
    int to, weight;
};

struct Subtree {
    int64_t sum_cost;
    int count;
    int branch_point_index;
};

vector<int> find_path(int N, int X, int Y, const vector<vector<Edge>>& adj) {
    vector<int> parent(N, -1);
    queue<int> q;
    q.push(X);
    parent[X] = X;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (const Edge& e : adj[u]) {
            int v = e.to;
            if (parent[v] == -1) {
                parent[v] = u;
                q.push(v);
                if (v == Y) {
                    while (!q.empty()) q.pop();
                    break;
                }
            }
        }
    }
    vector<int> path;
    int current = Y;
    while (current != X) {
        path.push_back(current);
        current = parent[current];
    }
    path.push_back(X);
    reverse(path.begin(), path.end());
    return path;
}

int max_score(int N, int X, int Y, long long K,
              vector<int> U, vector<int> V, vector<int> W) {
    vector<vector<Edge>> adj(N);
    for (int i = 0; i < N-1; ++i) {
        int u = U[i];
        int v = V[i];
        int w = W[i];
        adj[u].push_back({v, w});
        adj[v].push_back({u, w});
    }

    vector<int> path = find_path(N, X, Y, adj);
    int m = path.size();
    if (m == 0) return 0;

    unordered_set<int> backbone_nodes(path.begin(), path.end());
    unordered_map<int, int> node_to_index;
    for (int i = 0; i < m; ++i) {
        node_to_index[path[i]] = i;
    }

    vector<int64_t> a_all(N, 0), b_all(N, 0);
    queue<int> q;

    vector<bool> visited(N, false);
    q.push(X);
    visited[X] = true;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (const Edge& e : adj[u]) {
            int v = e.to;
            if (!visited[v]) {
                visited[v] = true;
                a_all[v] = a_all[u] + e.weight;
                q.push(v);
            }
        }
    }

    visited.assign(N, false);
    q.push(Y);
    visited[Y] = true;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (const Edge& e : adj[u]) {
            int v = e.to;
            if (!visited[v]) {
                visited[v] = true;
                b_all[v] = b_all[u] + e.weight;
                q.push(v);
            }
        }
    }

    vector<int64_t> a(m), b(m);
    for (int i = 0; i < m; ++i) {
        a[i] = a_all[path[i]];
        b[i] = b_all[path[i]];
    }

    vector<int64_t> sum_a(m + 1, 0), sum_b(m + 1, 0);
    for (int i = 0; i < m; ++i) {
        sum_a[i + 1] = sum_a[i] + a[i];
    }
    for (int i = m - 1; i >= 0; --i) {
        sum_b[i] = sum_b[i + 1] + b[i];
    }

    vector<int> branch_point(N, -1);
    for (int u : path) {
        branch_point[u] = node_to_index[u];
    }

    q.push(X);
    visited.assign(N, false);
    visited[X] = true;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (const Edge& e : adj[u]) {
            int v = e.to;
            if (!visited[v]) {
                visited[v] = true;
                branch_point[v] = branch_point[u];
                q.push(v);
            }
        }
    }

    unordered_map<int, vector<int>> subtrees_map;
    for (int u = 0; u < N; ++u) {
        if (backbone_nodes.count(u)) continue;
        int idx = branch_point[u];
        if (idx == -1) continue;
        subtrees_map[path[idx]].push_back(u);
    }

    vector<Subtree> x_subtres, y_subtres;
    for (const auto& entry : subtrees_map) {
        int branch_node = entry.first;
        int idx = node_to_index[branch_node];
        const vector<int>& nodes = entry.second;
        int64_t sum_a_sub = 0, sum_b_sub = 0;
        int count = nodes.size();
        for (int u : nodes) {
            sum_a_sub += a_all[u];
            sum_b_sub += b_all[u];
        }
        x_subtres.push_back({sum_a_sub, count, idx});
        y_subtres.push_back({sum_b_sub, count, idx});
    }

    sort(x_subtres.begin(), x_subtres.end(), [](const Subtree& s1, const Subtree& s2) {
        return (int64_t)s1.count * s2.sum_cost > (int64_t)s2.count * s1.sum_cost;
    });

    vector<int64_t> prefix_cost_x(1, 0);
    vector<int> prefix_benefit_x(1, 0);
    vector<int> prefix_max_j_x(1, -1);
    for (const Subtree& s : x_subtres) {
        prefix_cost_x.push_back(prefix_cost_x.back() + s.sum_cost);
        prefix_benefit_x.push_back(prefix_benefit_x.back() + s.count);
        prefix_max_j_x.push_back(max(prefix_max_j_x.back(), s.branch_point_index));
    }

    sort(y_subtres.begin(), y_subtres.end(), [](const Subtree& s1, const Subtree& s2) {
        return (int64_t)s1.count * s2.sum_cost > (int64_t)s2.count * s1.sum_cost;
    });

    vector<int64_t> prefix_cost_y(1, 0);
    vector<int> prefix_benefit_y(1, 0);
    vector<int> prefix_min_j_y(1, INT_MAX);
    for (const Subtree& s : y_subtres) {
        prefix_cost_y.push_back(prefix_cost_y.back() + s.sum_cost);
        prefix_benefit_y.push_back(prefix_benefit_y.back() + s.count);
        prefix_min_j_y.push_back(min(prefix_min_j_y.back(), s.branch_point_index));
    }

    int max_total = 0;
    for (int i = 0; i < m; ++i) {
        int64_t cost_backbone = sum_a[i] + sum_b[i + 1];
        if (cost_backbone > K) continue;
        int64_t rem = K - cost_backbone;
        if (rem < 0) continue;

        int left = 0, right = prefix_max_j_x.size() - 1;
        int k_max = 0;
        while (left <= right) {
            int mid = (left + right) / 2;
            if (prefix_max_j_x[mid] <= i) {
                k_max = mid;
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }

        left = 0, right = prefix_min_j_y.size() - 1;
        int m_max = 0;
        while (left <= right) {
            int mid = (left + right) / 2;
            if (prefix_min_j_y[mid] > i) {
                m_max = mid;
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }

        int best_benefit = 0;
        int current_k = min(k_max, (int)prefix_cost_x.size() - 1);
        int current_m = 0;

        while (current_k >= 0) {
            while (current_m < prefix_cost_y.size() && current_m <= m_max &&
                   prefix_cost_x[current_k] + prefix_cost_y[current_m] <= rem) {
                current_m++;
            }
            if (current_m > 0) {
                int total = prefix_benefit_x[current_k] + prefix_benefit_y[current_m - 1];
                if (total > best_benefit) {
                    best_benefit = total;
                }
            }
            current_k--;
        }

        int total_nodes = (i + 1) + (m - (i + 1));
        int total = total_nodes + best_benefit;
        if (total > max_total) {
            max_total = total;
        }
    }

    return max_total;
}

