    b_T.assign(n + 1, 0);
    b_C.assign(n + 1, 0);
    for (int i = 0; i < n; ++i) {
        b_A[i+1] = b_A[i] + (b[i] == 'A');
        b_T[i+1] = b_T[i] + (b[i] == 'T');
        b_C[i+1] = b_C[i] + (b[i] == 'C');
    }
}

int get_distance(int x, int y) {
    int a_A_cnt = a_A[y+1] - a_A[x];
    int a_T_cnt = a_T[y+1] - a_T[x];
    int a_C_cnt = a_C[y+1] - a_C[x];
    int b_A_cnt = b_A[y+1] - b_A[x];
    int b_T_cnt = b_T[y+1] - b_T[x];
    int b_C_cnt = b_C[y+1] - b_C[x];

    if (a_A_cnt != b_A_cnt || a_T_cnt != b_T_cnt || a_C_cnt != b_C_cnt) {
        return -1;
    }

    int count[3][3] = {0};
    for (int i = x; i <= y; ++i) {
        char a_char = a_str[i];
        char b_char = b_str[i];
        if (a_char != b_char) {
            int c1, c2;
            if (a_char == 'A') c1 = 0;
            else if (a_char == 'T') c1 = 1;
            else c1 = 2;

            if (b_char == 'A') c2 = 0;
            else if (b_char == 'T') c2 = 1;
            else c2 = 2;

            count[c1][c2]++;
        }
    }

    int m = 0;
    for (int i = 0; i < 3; ++i)
        for (int j = 0; j < 3; ++j)
            m += count[i][j];
    if (m == 0) return 0;

    int cycle_count = 0;
    for (int c1 = 0; c1 < 3; ++c1) {
        for (int c2 = 0; c2 < 3; ++c2) {
            while (count[c1][c2] > 0) {
                count[c1][c2]--;
                cycle_count++;
                int current = c2;
                while (current != c1) {
                    bool found = false;
                    for (int c3 = 0; c3 < 3; ++c3) {
                        if (count[current][c3] > 0) {
                            count[current][c3]--;
                            current = c3;
                            found = true;
                            break;
                        }
                    }
                    if (!found) break;
                }
            }
        }
    }
    return m - cycle_count;
}

