using namespace std;

int compute_A(int k, int N, vector<bool>& inside) {
    // Reset the machine to empty
    for (int i = 0; i < N; ++i) {
        if (inside[i]) {
            move_outside(i);
            inside[i] = false;
        }
    }

    int a = 0;
    for (int i = 0; i < N; ++i) {
        move_inside(i);
        inside[i] = true;
        int current_max = press_button();
        if (current_max > k) {
            move_outside(i);
            inside[i] = false;
        } else {
            a++;
        }
    }
    return a;
}

int min_cardinality(int N) {
    vector<bool> inside(N, false);
    int T = compute_A(1, N, inside);
    if (T == N) return 1; // All insects are of different types

    for (int k = 1; k <= N; ++k) {
        int a_prev = (k == 0) ? 0 : compute_A(k-1, N, inside);
        int a_curr = compute_A(k, N, inside);
        int a_next = compute_A(k+1, N, inside);

        int types_ge_k = a_curr - a_prev;
        int types_ge_k_plus_1 = a_next - a_curr;
        if (types_ge_k > types_ge_k_plus_1) {
            return k;
        }
    }
    return N; // This line is theoretically unreachable
}

#include <bits/stdc++.h>
using namespace std;

const int MOD = 998244353;
const int MAX = 2e5 + 5;

long long fact[MAX], inv_fact[MAX];

long long pow_mod(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

void precompute() {
    fact[0] = 1;
    for (int i = 1; i < MAX; ++i) {
        fact[i] = fact[i - 1] * i % MOD;
    }
    inv_fact[MAX - 1] = pow_mod(fact[MAX - 1], MOD - 2);
    for (int i = MAX - 2; i >= 0; --i) {
        inv_fact[i] = inv_fact[i + 1] * (i + 1) % MOD;
    }
}

long long comb(int m, int k) {
    if (m < 0 || k < 0 || k > m) return 0;
    return fact[m] * inv_fact[k] % MOD * inv_fact[m - k] % MOD;
}

int main() {
    precompute();
    int n, m;
    cin >> n >> m;
    if (n < 3) {
        cout << 0 << endl;
        return 0;
    }
    int k = n - 1;
    if (m < k) {
        cout << 0 << endl;
        return 0;
    }
    long long c = comb(m, k);
    long long pow2 = pow_mod(2, n - 3);
    long long res = ((n - 2) * pow2 % MOD) * c % MOD;
    cout << res << endl;
    return 0;
}

