int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    vector<Pair> up_pairs, down_pairs;

    for (int i = 1; i <= n; ++i) {
        int a, b;
        cin >> a >> b;
        if (a < b) {
            up_pairs.emplace_back(a, b, i);
        } else {
            down_pairs.emplace_back(a, b, i);
        }
    }

    vector<int> up_sequence;
    if (!up_pairs.empty()) {
        sort(up_pairs.begin(), up_pairs.end(), [](const Pair& p1, const Pair& p2) {
            return p1.b < p2.b;
        });
        up_sequence.push_back(up_pairs[0].idx);
        int current_end = up_pairs[0].b;
        for (size_t i = 1; i < up_pairs.size(); ++i) {
            if (up_pairs[i].a < current_end) {
                up_sequence.push_back(up_pairs[i].idx);
                current_end = up_pairs[i].b;
            }
        }
    }

    vector<int> down_sequence;
    if (!down_pairs.empty()) {
        sort(down_pairs.begin(), down_pairs.end(), [](const Pair& p1, const Pair& p2) {
