struct SegmentTreeNode {
    int min_l = INT_MAX;
    int video_idx = -1;
    SegmentTreeNode* left = nullptr;
    SegmentTreeNode* right = nullptr;
};

class SegmentTree {
private:
    const int MIN_VAL = 0;
    const int MAX_VAL = 1e9;

    void update(SegmentTreeNode* node, int l, int r, int idx, int val, int vidx) {
        if (l == r) {
            if (val < node->min_l) {
                node->min_l = val;
                node->video_idx = vidx;
            }
            return;
        }
        int mid = (l + r) / 2;
        if (idx <= mid) {
            if (!node->left) node->left = new SegmentTreeNode();
            update(node->left, l, mid, idx, val, vidx);
        } else {
            if (!node->right) node->right = new SegmentTreeNode();
            update(node->right, mid+1, r, idx, val, vidx);
        }
        node->min_l = min(
            (node->left ? node->left->min_l : INT_MAX),
            (node->right ? node->right->min_l : INT_MAX)
        );
        if (node->left && node->left->min_l == node->min_l) {
            node->video_idx = node->left->video_idx;
        } else if (node->right && node->right->min_l == node->min_l) {
            node->video_idx = node->right->video_idx;
        }
    }

    pair<int, int> query(SegmentTreeNode* node, int l, int r, int ql, int qr) {
        if (qr < l || ql > r || !node) return {INT_MAX, -1};
        if (ql <= l && r <= qr) return {node->min_l, node->video_idx};
        int mid = (l + r) / 2;
        auto left_res = query(node->left, l, mid, ql, qr);
        auto right_res = query(node->right, mid+1, r, ql, qr);
        if (left_res.first <= right_res.first) {
            return left_res;
        } else {
            return right_res;
        }
    }

public:
    SegmentTreeNode* root = new SegmentTreeNode();

    void insert(int l, int vidx) {
        update(root, MIN_VAL, MAX_VAL, l, l, vidx);
    }

    pair<int, int> getMin(int a, int b) {
        return query(root, MIN_VAL, MAX_VAL, a, b);
    }
};

struct Case3Node {
    vector<int> sorted_r;
    vector<int> max_lengths;
    vector<int> indices;
};

class Case3SegmentTree {
private:
    int n;
    vector<Case3Node> tree;

    void build(int node, int l, int r, const vector<Video>& videos) {
        if (l == r) {
            tree[node].sorted_r.push_back(videos[l].r);
            tree[node].max_lengths.push_back(videos[l].r - videos[l].l);
            tree[node].indices.push_back(videos[l].index);
            return;
        }
        int mid = (l + r) / 2;
        build(2*node+1, l, mid, videos);
        build(2*node+2, mid+1, r, videos);
        merge(tree[2*node+1].sorted_r.begin(), tree[2*node+1].sorted_r.end(),
              tree[2*node+2].sorted_r.begin(), tree[2*node+2].sorted_r.end(),
              back_inserter(tree[node].sorted_r));
        tree[node].max_lengths.resize(tree[node].sorted_r.size());
        tree[node].indices.resize(tree[node].sorted_r.size());
        int max_len = -1, best_idx = -1;
        for (size_t i = 0; i < tree[node].sorted_r.size(); ++i) {
            int current_len = (tree[node].sorted_r[i] - 
                videos[lower_bound(videos.begin(), videos.end(), Video{0, tree[node].sorted_r[i], 0}, 
                    [](const Video& a, const Video& b) { return a.r < b.r; }) - videos.begin()].l);
            if (current_len > max_len) {
                max_len = current_len;
                best_idx = tree[node].indices[i];
            }
            tree[node].max_lengths[i] = max_len;
            tree[node].indices[i] = best_idx;
        }
    }

    int query(int node, int l, int r, int ql, int qr, int b, int& best_idx) {
        if (qr < l || ql > r) return -1;
        if (ql <= l && r <= qr) {
            auto it = upper_bound(tree[node].sorted_r.begin(), tree[node].sorted_r.end(), b);
            if (it == tree[node].sorted_r.begin()) return -1;
            int pos = it - tree[node].sorted_r.begin() - 1;
            if (pos < 0) return -1;
            best_idx = tree[node].indices[pos];
            return tree[node].max_lengths[pos];
        }
        int mid = (l + r) / 2;
        int left_idx, right_idx;
        int left_res = query(2*node+1, l, mid, ql, qr, b, left_idx);
        int right_res = query(2*node+2, mid+1, r, ql, qr, b, right_idx);
        if (left_res > right_res) {
            best_idx = left_idx;
            return left_res;
        } else if (right_res > left_res) {
            best_idx = right_idx;
            return right_res;
        } else if (left_res != -1) {
            best_idx = left_res >= right_res ? left_idx : right_idx;
            return max(left_res, right_res);
        } else {
            return -1;
        }
    }

public:
    Case3SegmentTree(const vector<Video>& videos) {
        n = videos.size();
        tree.resize(4 * n);
        build(0, 0, n-1, videos);
    }

    pair<int, int> getMax(int a, int b) {
        int start = lower_bound(videos_sorted_l.begin(), videos_sorted_l.end(), a, 
            [](const Video& v, int val) { return v.l < val; }) - videos_sorted_l.begin();
        if (start >= n) return {-1, -1};
        int best_idx = -1;
        int max_len = query(0, 0, n-1, start, n-1, b, best_idx);
        return {max_len, best_idx};
    }

    vector<Video> videos_sorted_l;
};

vector<Video> videos_sorted_l;
vector<Video> videos_sorted_r;
vector<Channel> channels;
vector<int> sorted_l_l, sorted_l_r, sorted_l_indices, max_r_prefix, max_r_indices;
SegmentTree case2ST;
Case3SegmentTree* case3ST = nullptr;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    vector<Video> videos(n);
    for (int i = 0; i < n; ++i) {
        cin >> videos[i].l >> videos[i].r;
        videos[i].index = i + 1;
    }

    vector<Channel> channels(m);
    for (int i = 0; i < m; ++i) {
        cin >> channels[i].a >> channels[i].b >> channels[i].c;
        channels[i].index = i + 1;
    }

    // Preprocess for case4 and case1
    sort(videos.begin(), videos.end(), [](const Video& a, const Video& b) {
        return a.l < b.l;
    });
    videos_sorted_l = videos;

    sorted_l_l.resize(n);
    sorted_l_r.resize(n);
    sorted_l_indices.resize(n);
    for (int i = 0; i < n; ++i) {
        sorted_l_l[i] = videos[i].l;
        sorted_l_r[i] = videos[i].r;
        sorted_l_indices[i] = videos[i].index;
    }

    max_r_prefix.resize(n);
    max_r_indices.resize(n);
    max_r_prefix[0] = sorted_l_r[0];
    max_r_indices[0] = 0;
    for (int i = 1; i < n; ++i) {
        if (sorted_l_r[i] > max_r_prefix[i-1]) {
            max_r_prefix[i] = sorted_l_r[i];
            max_r_indices[i] = i;
        } else {
            max_r_prefix[i] = max_r_prefix[i-1];
            max_r_indices[i] = max_r_indices[i-1];
        }
    }

    // Preprocess for case2
    sort(videos.begin(), videos.end(), [](const Video& a, const Video& b) {
        return a.r > b.r;
    });
    videos_sorted_r = videos;

    vector<int> sorted_r_r(n);
    for (int i = 0; i < n; ++i) {
        sorted_r_r[i] = videos_sorted_r[i].r;
    }

    for (int i = 0; i < n; ++i) {
        case2ST.insert(videos_sorted_r[i].l, videos_sorted_r[i].index);
    }

    // Preprocess for case3
    sort(videos.begin(), videos.end(), [](const Video& a, const Video& b) {
        return a.l < b.l;
    });
    case3ST = new Case3SegmentTree(videos);
    case3ST->videos_sorted_l = videos;

    long long max_eff = 0;
    int best_video = -1, best_channel = -1;

    for (int j = 0; j < m; ++j) {
        int a = channels[j].a;
        int b = channels[j].b;
        int c = channels[j].c;

        if (a >= b) continue;

        long long case1 = 0, case2 = 0, case3 = 0, case4 = 0;
        int vid1 = -1, vid2 = -1, vid3 = -1, vid4 = -1;

        // Case4: exists video with l <=a and r >=b
        auto it = upper_bound(sorted_l_l.begin(), sorted_l_l.end(), a) - sorted_l_l.begin() - 1;
        if (it >= 0) {
            int max_r = max_r_prefix[it];
            if (max_r >= b) {
                case4 = (b - a) * 1LL * c;
                vid4 = sorted_l_indices[max_r_indices[it]];
            }
        }

        // Case1: max_r_case4 >=a
        if (it >= 0) {
            int max_r = max_r_prefix[it];
            if (max_r >= a) {
                int overlap = min(max_r, b) - a;
                if (overlap > 0) {
                    case1 = overlap * 1LL * c;
                    vid1 = sorted_l_indices[max_r_indices[it]];
                }
            }
        }

        // Case2: minimal l in [a, b] with r >=b
        int k = upper_bound(sorted_r_r.begin(), sorted_r_r.end(), b, greater<int>()) - sorted_r_r.begin();
        if (k > 0) {
            auto [min_l, vid] = case2ST.getMin(a, b);
            if (min_l != INT_MAX) {
                case2 = (b - min_l) * 1LL * c;
                vid2 = vid;
            }
        }

        // Case3: maximum (r-l) in [a <= l, r <=b]
        auto [max_len, vid] = case3ST->getMax(a, b);
        if (max_len > 0) {
            case3 = max_len * 1LL * c;
            vid3 = vid;
        }

        long long current_max = max({case1, case2, case3, case4});
        if (current_max > max_eff) {
            max_eff = current_max;
            if (case4 == current_max) {
                best_video = vid4;
                best_channel = channels[j].index;
            } else if (case1 == current_max) {
                best_video = vid1;
                best_channel = channels[j].index;
            } else if (case2 == current_max) {
                best_video = vid2;
                best_channel = channels[j].index;
            } else if (case3 == current_max) {
                best_video = vid3;
                best_channel = channels[j].index;
            }
        }
    }

    cout << max_eff << "\n";
    if (max_eff > 0) {
        cout << best_video << " " << best_channel << "\n";
    }

    delete case3ST;
    return 0;
}

