int main() {
    long long n, k;
    cin >> n >> k;

    if (k == 1) {
        cout << 1 << endl;
        return 0;
    } else if (k == 2) {
        cout << n << endl;
        return 0;
    }

    long long m = k - 2;
    m -= 1;

    int level = 0;
    while ((1LL << (level + 1)) - 1 <= m) {
        level++;
    }

    long long remaining = m - ((1LL << level) - 1);
    long long low = 1, high = n;

    for (int i = level - 1; i >= 0; --i) {
        long long mid = (low + high) / 2;
        long long bit = (remaining >> i) & 1;
        if (bit) {
            low = mid;
        } else {
            high = mid;
        }
    }

    cout << (low + high) / 2 << endl;

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

using ll = long long;
const int MOD = 1e9 + 7;
const int N = 201;

vector<int> adj[N];
vector<pair<int, int>> edges;
int sz[N][N][2];
int inv[N];

int modpow(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) res = (ll)res * a % MOD;
        a = (ll)a * a % MOD;
        b >>= 1;
    }
    return res;
}

void dfs(int u, int p, int root, int block, int comp) {
    sz[root][block][comp]++;
    for (int v : adj[u]) {
        if (v == p || v == block) continue;
        dfs(v, u, root, block, comp);
    }
}

void precompute_sz(int n) {
    for (auto &e : edges) {
        int u = e.first, v = e.second;
        dfs(u, v, u, v, 0);
        dfs(v, u, v, u, 1);
    }
}

vector<int> get_path(int s, int t, int n) {
    vector<int> parent(n + 1, -1);
    queue<int> q;
    q.push(s);
    parent[s] = -2;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (parent[v] == -1) {
                parent[v] = u;
                q.push(v);
                if (v == t) {
                    vector<int> path;
                    while (v != -2) {
                        path.push_back(v);
                        v = parent[v];
                    }
                    reverse(path.begin(), path.end());
                    return path;
                }
            }
        }
    }
    return {};
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    for (int i = 1; i < n; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
        edges.emplace_back(u, v);
        edges.emplace_back(v, u);
    }

    precompute_sz(n);

    for (int i = 1; i <= n; ++i) {
        inv[i] = modpow(i, MOD - 2);
    }

    ll res = 0;

    for (int u = 1; u <= n; ++u) {
        for (int v = 1; v < u; ++v) {
            vector<int> path = get_path(u, v, n);
            for (int s = 1; s <= n; ++s) {
                vector<int> path_su = get_path(s, u, n);
                vector<int> path_sv = get_path(s, v, n);
                int lca = s;
                int i = 0, j = 0;
                while (i < path_su.size() && j < path_sv.size() && path_su[i] == path_sv[j]) {
                    lca = path_su[i];
                    i++;
                    j++;
                }
                int idx = find(path.begin(), path.end(), lca) - path.begin();
                ll prob = 1;
                for (int k = idx; k < path.size() - 1; ++k) {
                    int a = path[k], b = path[k + 1];
                    int s_a = sz[a][b][0], s_b = sz[b][a][1];
                    int den = (s_a + s_b) % MOD;
                    if (b == path[k + 1]) {
                        prob = prob * s_a % MOD;
                        prob = prob * inv[den] % MOD;
                    }
                }
                res = (res + prob * inv[n]) % MOD;
            }
        }
    }

    cout << res << endl;

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

vector<int> bfs(const vector<vector<int>>& adj, int start) {
    int n = adj.size() - 1;
    vector<int> dist(n + 1, -1);
    queue<int> q;
    q.push(start);
    dist[start] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (dist[v] == -1) {
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }
    return dist;
}

int find_diameter_lower_bound(const vector<vector<int>>& adj) {
    int n = adj.size() - 1;
    if (n == 0) return 0;
    int start = 1;
    vector<int> dist = bfs(adj, start);
    for (int i = 1; i <= n; ++i) {
        if (dist[i] > dist[start]) start = i;
    }
    dist = bfs(adj, start);
    int max_dist = *max_element(dist.begin(), dist.end());
    return max_dist;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m, q;
    cin >> n >> m >> q;

    vector<vector<int>> adj(n + 1);
    for (int i = 0; i < m; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    int L = find_diameter_lower_bound(adj);

