#include <bits/stdc++.h>
using namespace std;

struct MergeSortTree {
    vector<vector<int>> tree;

    MergeSortTree(const vector<int>& data) {
        int n = data.size();
        tree.resize(4 * n);
        build(0, 0, n-1, data);
    }

    void build(int node, int l, int r, const vector<int>& data) {
        if (l == r) {
            tree[node] = {data[l]};
            return;
        }
        int mid = (l + r) / 2;
        build(2*node+1, l, mid, data);
        build(2*node+2, mid+1, r, data);
        merge(tree[2*node+1].begin(), tree[2*node+1].end(),
              tree[2*node+2].begin(), tree[2*node+2].end(),
              back_inserter(tree[node]));
    }

    int query(int node, int l, int r, int ql, int qr, int a) {
        if (qr < l || ql > r) return 0;
        if (ql <= l && r <= qr) {
            auto& vec = tree[node];
            return vec.end() - upper_bound(vec.begin(), vec.end(), a);
        }
        int mid = (l + r) / 2;
        return query(2*node+1, l, mid, ql, qr, a) +
               query(2*node+2, mid+1, r, ql, qr, a);
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, p;
    cin >> n >> p;

    vector<int> size_S(n+1);
    map<pair<int, int>, int> coder_pairs;

    for (int i = 0; i < n; ++i) {
        int x, y;
        cin >> x >> y;
        size_S[x]++;
        size_S[y]++;
        if (x > y) swap(x, y);
        coder_pairs[{x, y}]++;
    }

    vector<pair<int, int>> sorted_S;
    for (int i = 1; i <= n; ++i) {
        sorted_S.emplace_back(size_S[i], i);
    }
    sort(sorted_S.begin(), sorted_S.end());

    vector<int> indices;
    for (auto& p : sorted_S) {
        indices.push_back(p.second);
    }

    MergeSortTree mst(indices);
    int total_pairs = 0;
    int m = indices.size();

    for (int a = 1; a <= n-1; ++a) {
        int x = p - size_S[a];
        auto it = lower_bound(sorted_S.begin(), sorted_S.end(), make_pair(x, 0),
            [](const pair<int, int>& lhs, const pair<int, int>& rhs) {
                return lhs.first < rhs.first;
            });
        int pos = it - sorted_S.begin();
        if (pos >= m) continue;
        int cnt = mst.query(0, 0, m-1, pos, m-1, a);
        total_pairs += cnt;
    }

    int coder_pairs_sum_ge_p = 0;
    int coder_pairs_valid = 0;

    for (const auto& entry : coder_pairs) {
        int u = entry.first.first;
        int v = entry.first.second;
        int count_ab = entry.second;

        int sum_uv = size_S[u] + size_S[v];
        if (sum_uv >= p) {
            coder_pairs_sum_ge_p++;
        }
        if (sum_uv - count_ab >= p) {
            coder_pairs_valid++;
        }
    }

    int answer = (total_pairs - coder_pairs_sum_ge_p) + coder_pairs_valid;
    cout << answer << '\n';

    return 0;
}

