        for (auto &x : a) cin >> x;

        vector<long long> evens, odds;
        for (auto x : a) {
            if (x % 2 == 0) evens.push_back(x);
            else odds.push_back(x);
        }

        int m = evens.size();
        int k = odds.size();

        if (k == 0) {
            long long sum_steps = 0;
            long long sum_evens = 0;
            for (auto x : evens) {
                sum_steps += count_steps(x);
                sum_evens += x;
            }
            int s = count_steps(sum_evens);
            int group_steps = (m - 1) + s;
            cout << min(sum_steps, group_steps) << '\n';
        } else {
            if (m <= k) {
                cout << m << '\n';
            } else {
                vector<pair<int, long long>> sorted_evens;
                for (auto x : evens) {
                    sorted_evens.emplace_back(count_steps(x), x);
                }
                sort(sorted_evens.rbegin(), sorted_evens.rend());

                long long sum_steps_remaining = 0;
                long long sum_remaining = 0;
                for (int i = k; i < m; ++i) {
                    sum_steps_remaining += sorted_evens[i].first;
                    sum_remaining += sorted_evens[i].second;
                }
                int s_remaining = count_steps(sum_remaining);
                int group_steps = min(sum_steps_remaining, (m - k - 1) + s_remaining);
                cout << k + group_steps << '\n';
            }
        }
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

using ll = long long;
const int MOD = 998244353;

ll modinv(ll a) {
    ll b = MOD, u = 1, v = 0;
    while (b) {
        ll t = a / b;
        a -= t * b; swap(a, b);
        u -= t * v; swap(u, v);
    }
    return (u % MOD + MOD) % MOD;
}

void solve() {
    int n;
    cin >> n;
    vector<ll> s(n);
    for (auto& x : s) cin >> x;

    ll ans = 0;
    ll cnt = 0;
    map<array<ll, 2>, ll> freq;

    for (ll a : s) {
        for (ll b : s) {
            ll c = a | b;
            ll steps = 0;
            if (a == b) {
                if (a == 0) steps = 1;
                else steps = 2;
            } else {
                ll mask_a = (c & ~a);
                if (mask_a > a) {
                    steps = 1;
                } else {
                    ll mask_b = (c & ~b);
                    if (mask_b > b) {
                        steps = 2;
                    } else {
                        steps = 3;
                    }
                }
            }
            ans = (ans + steps) % MOD;
            cnt++;
        }
    }

    ll inv = modinv(cnt);
    cout << (ans * inv) % MOD << '\n';
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        solve();
    }

    return 0;
}

