struct Run { char type; int length; };

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int t; cin >> t;
    while (t--) {
        int n, k; string s;
        cin >> n >> k >> s;
        vector<Run> runs;
        if (n == 0) { cout << "0\n"; continue; }

        char current = s[0];
        int cnt = 1;
        for (int i = 1; i < n; ++i) {
            if (s[i] == current) cnt++;
            else {
                runs.push_back({current, cnt});
                current = s[i];
                cnt = 1;
            }
        }
        runs.push_back({current, cnt});

        bool has_w = false;
        for (auto &r : runs) if (r.type == 'W') { has_w = true; break; }
        if (!has_w) {
            if (k == 0) cout << "0\n";
            else cout << (min(k, n) >= 1 ? 2 * min(k, n) - 1 : 0) << '\n';
            continue;
        }

        int score = 0, total_L = 0;
        for (auto &r : runs) {
            if (r.type == 'W') score += 2 * r.length - 1;
            else total_L += r.length;
        }

        vector<int> gaps;
        for (int i = 1; i < (int)runs.size() - 1; ++i)
            if (runs[i].type == 'L' && runs[i-1].type == 'W' && runs[i+1].type == 'W')
                gaps.push_back(runs[i].length);

        sort(gaps.begin(), gaps.end());
        int merged_L = 0;
        for (int g : gaps) {
            if (k >= g) {
                score += 2 * g + 1;
                k -= g;
                merged_L += g;
            } else break;
        }

        int left = (runs.front().type == 'L') ? runs.front().length : 0;
        int right = (runs.back().type == 'L') ? runs.back().length : 0;
        int adj = left + right;
        int non_adj = total_L - merged_L - adj;

        int flips_adj = min(k, adj);
        score += 2 * flips_adj;
        k -= flips_adj;
        score += min(k, non_adj);

        cout << score << '\n';
    }
    return 0;
}

