long long max_weights(int N, int M, vector<int> X, vector<int> Y,
                      vector<int> W) {
    vector<pair<int, int>> group0, group1;
    for (int i = 0; i < M; ++i) {
        if (X[i] == 0) {
            group0.emplace_back(Y[i], W[i]);
        } else if (X[i] == 1) {
            group1.emplace_back(Y[i], W[i]);
        }
    }

    sort(group0.begin(), group0.end());
    sort(group1.begin(), group1.end());

    vector<int> y0;
    vector<long long> prefix0;
    long long sum = 0;
    for (auto & p : group0) {
        y0.push_back(p.first);
        sum += p.second;
        prefix0.push_back(sum);
    }

    vector<int> y1;
    vector<long long> prefix1;
    sum = 0;
    for (auto & p : group1) {
        y1.push_back(p.first);
        sum += p.second;
        prefix1.push_back(sum);
    }

    long long sum_group1 = sum;

    long long max_val = 0;

    // Compute scenario1: group0 under k, group1 over k
    for (int k = 0; k <= N; ++k) {
        long long s0 = 0;
        if (!y0.empty()) {
            auto it = lower_bound(y0.begin(), y0.end(), k);
            int idx = it - y0.begin();
            if (idx > 0) {
                s0 = prefix0[idx-1];
            }
        }

        long long s1 = 0;
        if (!y1.empty()) {
            auto it = lower_bound(y1.begin(), y1.end(), k);
            int idx = it - y1.begin();
            s1 = sum_group1 - (idx > 0 ? prefix1[idx-1] : 0);
        }

        max_val = max(max_val, s0 + s1);
    }

    // Compute scenario2: group1 can be captured by k0 > Y[i] and k1 <= Y[i]
    long long sum_group1_all = sum_group1;
    max_val = max(max_val, sum_group1_all);

    return max_val;
}

#include <iostream>
#include <vector>
#include <cmath>
using namespace std;

const int MAX = 1e6 + 5;
vector<long long> dp(MAX, -1);

long long compute_sum(int n) {
    if (n == 0) return 0;
    if (dp[n] != -1) return dp[n];
    int r = (sqrt(8LL*n + 1) - 1)/2;
    while (r*(r+1)/2 >= n) r--;
    r++;
    int a = n - r;
    int b = a + 1;
    if (a <= 0) {
        return dp[n] = 1LL * n * n;
    }
    long long sum = 1LL * n * n + compute_sum(a) + compute_sum(b);
    int r_a = (sqrt(8LL*a + 1) - 1)/2;
    while (r_a*(r_a+1)/2 >= a) r_a--;
    r_a++;
    int c = a - r_a;
    if (c > 0) {
        sum -= compute_sum(c);
    }
    return dp[n] = sum;
}

