    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    dp[1] = 1;
    int t;
    cin >> t;
    while (t--) {
        int n;
        cin >> n;
        cout << compute_sum(n) << '\n';
    }
    return 0;
}

