#include <bits/stdc++.h>
using namespace std;

const int INF = 1e9 + 5;

bool solve() {
    int n;
    cin >> n;
    vector<int> a(n);
    for (auto &x : a) cin >> x;

    vector<int> left_prev(n + 1, -INF);
    left_prev[0] = -INF;
    for (int i = 1; i <= n; ++i) {
        if (left_prev[i-1] == -INF) {
            left_prev[i] = -INF;
            continue;
        }
        int current = a[i-1];
        int prev = left_prev[i-1];
        if (current > prev) {
            left_prev[i] = current;
        } else if (current + 1 > prev) {
            left_prev[i] = current + 1;
        } else {
            left_prev[i] = -INF;
        }
    }

    vector<int> right_next(n + 1, -INF);
    right_next[n] = INF;
    for (int i = n - 1; i >= 0; --i) {
        if (right_next[i+1] == -INF) {
            right_next[i] = -INF;
            continue;
        }
        int current = a[i];
        int next_val = right_next[i+1];
        int max_possible = current + 1;
        if (max_possible < next_val) {
            right_next[i] = max_possible;
        } else if (current < next_val) {
            right_next[i] = current;
        } else {
            right_next[i] = -INF;
        }
    }

    for (int i = 0; i < n; ++i) {
        bool left_valid = (i == 0) ? true : (left_prev[i] != -INF);
        bool right_valid = (i == n-1) ? true : (right_next[i+1] != -INF);

        if (i == 0 && right_valid) {
            return true;
        }
        if (i == n-1 && left_valid) {
            return true;
        }
        if (left_valid && right_valid) {
            int left_val = (i == 0) ? -INF : left_prev[i];
            int right_val = (i == n-1) ? INF : right_next[i+1];
            if (left_val < right_val) {
                return true;
            }
        }
    }
    return false;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        cout << (solve() ? "YES" : "NO") << '\n';
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int N = 2e5 + 5;

vector<int> adj_bipartition[N];
int color[N];

vector<int> adj_dag[N];
int in_degree[N];

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;

    vector<tuple<int, int, int>> constraints;

    for (int i = 0; i < m; ++i) {
        int type, u, v;
        cin >> type >> u >> v;
        constraints.emplace_back(type, u, v);
        adj_bipartition[u].push_back(v);
        adj_bipartition[v].push_back(u);
    }

    // Bipartition check
    bool is_bipartite = true;
    memset(color, -1, sizeof(color));
    for (int i = 1; i <= n; ++i) {
        if (color[i] == -1) {
            queue<int> q;
            q.push(i);
            color[i] = 0;
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v : adj_bipartition[u]) {
                    if (color[v] == -1) {
                        color[v] = color[u] ^ 1;
                        q.push(v);
                    } else if (color[v] == color[u]) {
                        is_bipartite = false;
                        break;
                    }
                }
                if (!is_bipartite) break;
            }
            if (!is_bipartite) break;
        }
    }

    if (!is_bipartite) {
        cout << "NO\n";
        return 0;
    }

    // Build directed graph based on constraints and colors
    for (auto [type, u, v] : constraints) {
        if (color[u] == 0) { // u is R, v is L
            if (type == 1) {
                adj_dag[v].push_back(u);
                in_degree[u]++;
            } else {
                adj_dag[u].push_back(v);
                in_degree[v]++;
            }
        } else { // u is L, v is R
            if (type == 1) {
                adj_dag[u].push_back(v);
                in_degree[v]++;
            } else {
                adj_dag[v].push_back(u);
                in_degree[u]++;
            }
        }
    }

    // Kahn's algorithm for topological sort
    queue<int> q;
    for (int i = 1; i <= n; ++i) {
        if (in_degree[i] == 0) {
            q.push(i);
        }
    }

    vector<int> top_order;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        top_order.push_back(u);
        for (int v : adj_dag[u]) {
            in_degree[v]--;
            if (in_degree[v] == 0) {
                q.push(v);
            }
        }
    }

    if (top_order.size() != n) {
        cout << "NO\n";
        return 0;
    }

    // Assign x values based on topological order
    vector<int> x(n + 1);
    int current_x = 0;
    for (int u : top_order) {
        x[u] = current_x++;
    }

    // Output
    cout << "YES\n";
    for (int i = 1; i <= n; ++i) {
        cout << (color[i] == 0 ? 'R' : 'L') << ' ' << x[i] << '\n';
    }

    return 0;
}

