bool solve(const string &s) {
    int n = s.length();
    int start = 0;
    bool in_non_w = false;
    for (int i = 0; i <= n; ++i) {
        if (i == n || s[i] == 'W') {
            if (in_non_w) {
                int len = i - start;
                if (len == 1) return false;
                bool has_r = false, has_b = false;
                for (int j = start; j < i; ++j) {
                    if (s[j] == 'R') has_r = true;
                    else has_b = true;
                }
                if (!has_r || !has_b) return false;
                in_non_w = false;
            }
        } else {
            if (!in_non_w) {
                in_non_w = true;
                start = i;
            }
        }
    }
    return true;
}

