#include <bits/stdc++.h>
using namespace std;

const int MOD = 998244353;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int N;
    cin >> N;
    vector<int> A(N);
    for (int& a : A) cin >> a;

    unordered_map<int, int> dp;
    dp[0] = 1;
    for (int num : A) {
        unordered_map<int, int> tmp;
        for (auto [x, cnt] : dp) {
            if (tmp.find(x) != tmp.end())
                tmp[x] = (tmp[x] + cnt) % MOD;
            else
                tmp[x] = cnt % MOD;
            int nx = x ^ num;
            long long add = 2LL * cnt % MOD;
            if (tmp.find(nx) != tmp.end())
                tmp[nx] = (tmp[nx] + add) % MOD;
            else
                tmp[nx] = add % MOD;
        }
        dp.swap(tmp);
    }

    cout << (dp[0] % MOD + MOD) % MOD << "\n";

    return 0;
}

