using namespace std;

int N_assistant, K_assistant;
int N_magician, K_magician;

vector<int> get_permutation_by_rank(const vector<int>& T, int rank) {
    int n = T.size();
    vector<int> fact(n, 1);
    for (int i = n-2; i >= 0; --i) {
        fact[i] = fact[i+1] * (n - 1 - i);
    }
    vector<int> perm;
    vector<int> elements = T;
    for (int i = 0; i < n; ++i) {
        int f = fact[i];
        int cnt = rank / f;
        rank %= f;
        perm.push_back(elements[cnt]);
        elements.erase(elements.begin() + cnt);
    }
    return perm;
}

int get_lex_rank(const vector<int>& P, const vector<int>& T) {
    int n = P.size();
    vector<int> fact(n, 1);
    for (int i = n-2; i >= 0; --i) {
        fact[i] = fact[i+1] * (n - 1 - i);
    }
    int rank = 0;
    vector<int> elements = T;
    for (int i = 0; i < n; ++i) {
        auto pos = find(elements.begin(), elements.end(), P[i]);
        int cnt = distance(elements.begin(), pos);
        rank += cnt * fact[i];
        elements.erase(pos);
    }
    return rank;
}

void init_assistant(int N, int K) {
    N_assistant = N;
    K_assistant = K;
}

vector<int> choose_cards(vector<int> cards) {
    int m = 0; // Always choose the first card for simplicity
    int x = cards[m];
    vector<int> T;
    for (int c : cards) {
        if (c != x) {
            T.push_back(c);
        }
    }
    sort(T.begin(), T.end());
    vector<int> perm = get_permutation_by_rank(T, m);
    return perm;
}

void init_magician(int N, int K) {
    N_magician = N;
    K_magician = K;
}

int find_discarded_card(vector<int> cards) {
    set<int> present(cards.begin(), cards.end());
    for (int x = 1; x <= N_magician; ++x) {
        if (present.count(x)) continue;
        vector<int> scandidate = cards;
        scandidate.push_back(x);
        sort(scandidate.begin(), scandidate.end());
        auto it = find(scandidate.begin(), scandidate.end(), x);
        int m_candidate = it - scandidate.begin();
        vector<int> T_candidate;
        for (int c : scandidate) {
            if (c != x) {
                T_candidate.push_back(c);
            }
        }
        sort(T_candidate.begin(), T_candidate.end());
        int rank = get_lex_rank(cards, T_candidate);
        if (rank % 6 == m_candidate) {
            return x;
        }
    }
    return -1; // Should not reach here
}

