vector<int> find_edges(int a, int b, const vector<int>& U, const vector<int>& V) {
    vector<int> res;
    for (int i = 0; i < U.size(); ++i) {
        if ((U[i] == a && V[i] == b) || (U[i] == b && V[i] == a)) {
            res.push_back(i);
        }
    }
    return res;
}

std::variant<bool, std::vector<int>> find_journey(
    int N, int M, std::vector<int> U, std::vector<int> V) {

    if (N == 2) {
        vector<int> edges01 = find_edges(0, 1, U, V);
        if (edges01.size() != 2) return false;
        int A = -1, D = -1;
        for (int e : edges01) {
            if (U[e] == 0) A = e;
            else D = e;
        }
        return vector<int>{A, D, A, D};
    } else if (N >= 3) {
        int x = 1, y = 2;
        vector<int> edges0x = find_edges(0, x, U, V);
        if (edges0x.size() != 2) return false;
        int A = -1, D = -1;
        for (int e : edges0x) {
            if (U[e] == 0) A = e;
            else D = e;
        }
        vector<int> edgesxy = find_edges(x, y, U, V);
        if (edgesxy.size() != 2) return false;
        int B = -1, C = -1;
        for (int e : edgesxy) {
            if (U[e] == x) B = e;
            else C = e;
        }
        return vector<int>{A, B, C, D, A, B, C, D};
    }
    return false;
}

