#include <bits/stdc++.h>
using namespace std;

struct Ring {
    int a, b, h;
};

bool compareRing(const Ring& x, const Ring& y) {
    return x.b > y.b;
}

struct FenwickMax {
    vector<long long> tree;
    int size;

    FenwickMax(int n) : size(n), tree(n + 2, 0) {}

    void update(int idx, long long value) {
        idx++;
        while (idx <= size) {
            if (value > tree[idx]) {
                tree[idx] = value;
            } else {
                break;
            }
            idx += idx & -idx;
        }
    }

    long long query(int idx) {
        idx++;
        long long res = 0;
        while (idx > 0) {
            res = max(res, tree[idx]);
            idx -= idx & -idx;
        }
        return res;
    }
};

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    vector<Ring> rings(n);
    for (auto& ring : rings) {
        cin >> ring.a >> ring.b >> ring.h;
    }

    sort(rings.begin(), rings.end(), compareRing);

    vector<int> values;
    for (const auto& ring : rings) {
        values.push_back(ring.a);
        values.push_back(ring.b);
    }
    sort(values.begin(), values.end());
    values.erase(unique(values.begin(), values.end()), values.end());

    FenwickMax fenwick(values.size());

    long long max_height = 0;
    for (const auto& ring : rings) {
        int x = ring.b;
        auto it = lower_bound(values.begin(), values.end(), x);
        int k = (it == values.begin()) ? -1 : (prev(it) - values.begin());

        long long current = (k >= 0) ? fenwick.query(k) : 0;
        long long dp = ring.h + current;
        max_height = max(max_height, dp);

        int a_idx = lower_bound(values.begin(), values.end(), ring.a) - values.begin();
        fenwick.update(a_idx, dp);
    }

    cout << max_height << '\n';

    return 0;
}

#include <bits/stdc++.h>
#include "closing.h"
#include <vector>
#include <queue>
#include <algorithm>
#include <tuple>
using namespace std;

struct Subtree {
    long long sum_a_x = 0, sum_a_y = 0, sum_max = 0;
    int count = 0;
};

vector<int> get_path(int N, int X, int Y, const vector<vector<pair<int, int>>>& adj) {
    vector<int> parent(N, -1);
    queue<int> q;
    q.push(X);
    parent[X] = X;
    while (!q.empty()) {
        int u = q.front(); q.pop();
        for (auto [v, w] : adj[u]) {
            if (parent[v] == -1) {
                parent[v] = u;
                q.push(v);
            }
        }
    }
    vector<int> path;
    int curr = Y;
    while (true) {
        path.push_back(curr);
        if (curr == X) break;
        curr = parent[curr];
    }
    reverse(path.begin(), path.end());
    return path;
}

void bfs(int start, const vector<vector<pair<int, int>>>& adj, vector<long long>& dist) {
    int N = adj.size();
    dist.assign(N, -1);
    queue<int> q;
    q.push(start);
    dist[start] = 0;
    while (!q.empty()) {
        int u = q.front(); q.pop();
        for (auto [v, w] : adj[u]) {
            if (dist[v] == -1) {
                dist[v] = dist[u] + w;
                q.push(v);
            }
        }
    }
}

int max_score(int N, int X, int Y, long long K, vector<int> U, vector<int> V, vector<int> W) {
    vector<vector<pair<int, int>>> adj(N);
    for (int i = 0; i < N-1; ++i) {
        int u = U[i], v = V[i], w = W[i];
        adj[u].emplace_back(v, w);
        adj[v].emplace_back(u, w);
    }

    vector<int> path = get_path(N, X, Y, adj);
    int m = path.size() - 1;

    vector<long long> a_x(N), a_y(N);
    bfs(X, adj, a_x);
    bfs(Y, adj, a_y);

    vector<bool> on_path(N, false);
    for (int u : path) on_path[u] = true;

    vector<int> branch(N, -1);
    vector<int> parentX(N, -1);
    {
        queue<int> q;
        q.push(X);
        parentX[X] = X;
        while (!q.empty()) {
            int u = q.front(); q.pop();
            for (auto [v, w] : adj[u]) {
                if (parentX[v] == -1) {
                    parentX[v] = u;
                    q.push(v);
                }
            }
        }
    }
    for (int u = 0; u < N; ++u) {
        if (on_path[u]) {
            branch[u] = u;
            continue;
        }
        int curr = u;
        while (curr != X && !on_path[curr]) {
            curr = parentX[curr];
        }
        branch[u] = curr;
    }

    vector<Subtree> subtrees(N);
    vector<bool> processed(N, false);
    for (int u : path) processed[u] = true;

    for (int u = 0; u < N; ++u) {
        if (processed[u] || on_path[u]) continue;
        int k = branch[u];
        if (k == -1) continue;

        vector<int> stack;
        stack.push_back(u);
        processed[u] = true;
        Subtree st;

        while (!stack.empty()) {
            int v = stack.back();
            stack.pop_back();

            st.sum_a_x += a_x[v];
            st.sum_a_y += a_y[v];
            st.sum_max += max(a_x[v], a_y[v]);
            st.count++;

            for (auto [w, _] : adj[v]) {
                if (!processed[w] && !on_path[w] && branch[w] == k) {
                    processed[w] = true;
                    stack.push_back(w);
                }
            }
        }

        subtrees[k] = st;
    }

    vector<long long> dx(path.size()), dy(path.size());
    for (int i = 0; i < path.size(); ++i) {
        dx[i] = a_x[path[i]];
        dy[i] = a_y[path[i]];
    }

    vector<long long> prefix_dx(path.size() + 1, 0);
    for (int i = 0; i < path.size(); ++i) {
        prefix_dx[i+1] = prefix_dx[i] + dx[i];
    }
    vector<long long> suffix_dy(path.size() + 1, 0);
    for (int i = path.size() - 1; i >= 0; --i) {
        suffix_dy[i] = suffix_dy[i+1] + dy[i];
    }

    vector<long long> max_dx_dy(path.size());
    for (int i = 0; i < path.size(); ++i) {
        max_dx_dy[i] = max(dx[i], dy[i]);
    }
    vector<long long> prefix_max(path.size() + 1, 0);
    for (int i = 0; i < path.size(); ++i) {
        prefix_max[i+1] = prefix_max[i] + max_dx_dy[i];
    }

    int max_contrib = 0;

    for (int i = 0; i < path.size(); ++i) {
        for (int j = 0; j <= i; ++j) {
            long long cost = prefix_dx[j] + (prefix_max[i+1] - prefix_max[j]) + (suffix_dy[i+1]);
            if (cost > K) continue;

            int contrib = (i + 1) + (path.size() - j);
            long long remaining = K - cost;

            vector<pair<int, long long>> options;

            for (int k = 0; k < path.size(); ++k) {
                const Subtree& st = subtrees[path[k]];
                if (st.count == 0) continue;

                if (k < j) {
                    if (st.sum_a_x <= remaining) {
                        options.emplace_back(st.count, st.sum_a_x);
                    }
                } else if (k > i) {
                    if (st.sum_a_y <= remaining) {
                        options.emplace_back(st.count, st.sum_a_y);
                    }
                } else {
                    vector<pair<int, long long>> opts;
                    opts.emplace_back(0, 0);
                    if (st.sum_a_x <= remaining) opts.emplace_back(st.count, st.sum_a_x);
                    if (st.sum_a_y <= remaining) opts.emplace_back(st.count, st.sum_a_y);
                    if (st.sum_max <= remaining) opts.emplace_back(2 * st.count, st.sum_max);
                    int best_c = 0;
                    long long best_cost = 0;
                    for (auto [c, co] : opts) {
                        if (c > best_c || (c == best_c && co < best_cost)) {
                            best_c = c;
                            best_cost = co;
                        }
                    }
                    if (best_c > 0) {
                        options.emplace_back(best_c, best_cost);
                    }
                }
            }

            sort(options.begin(), options.end(), [](const pair<int, long long>& a, const pair<int, long long>& b) {
                if (a.second == 0) return true;
                if (b.second == 0) return false;
                return a.first * b.second > b.first * a.second;
            });

            long long curr_cost = 0;
            int curr_contrib = 0;
            for (auto [c, co] : options) {
                if (curr_cost + co <= remaining) {
                    curr_cost += co;
                    curr_contrib += c;
                } else if (co <= remaining - curr_cost) {
                    curr_contrib += c;
                    break;
                }
            }

            max_contrib = max(max_contrib, contrib + curr_contrib);
        }
    }

    return max_contrib;
}

