    cin.tie(nullptr);

    int n, k;
    cin >> n >> k;

    vector<int> d(n + 1);
    map<int, vector<int>> levels;
    int max_d = 0;

    for (int i = 1; i <= n; ++i) {
        cin >> d[i];
        levels[d[i]].push_back(i);
        max_d = max(max_d, d[i]);
    }

    // Check if root exists and is unique
    if (levels[0].size() != 1) {
        cout << -1 << '\n';
        return 0;
    }

    int root = levels[0][0];
    vector<pair<int, int>> edges;
    bool possible = true;

    for (int dist = 1; dist <= max_d; ++dist) {
        if (levels[dist].empty()) {
            possible = false;
            break;
        }

        int prev_dist = dist - 1;
        int prev_size = levels[prev_dist].size();
        int curr_size = levels[dist].size();

        if (prev_size == 0 || (prev_dist == 0 && prev_size > 1)) {
            possible = false;
            break;
        }

        int prev_ptr = 0;
        int edges_added = 0;

        for (int curr_v : levels[dist]) {
            if (prev_ptr >= prev_size) {
                possible = false;
                break;
            }

            int prev_v = levels[prev_dist][prev_ptr];
            edges.emplace_back(prev_v, curr_v);
            edges_added++;

            if (edges_added == k) {
                edges_added = 0;
                prev_ptr++;
            }
        }

        if (!possible) break;
    }

    if (!possible) {
        cout << -1 << '\n';
    } else {
        cout << edges.size() << '\n';
        for (auto& edge : edges) {
            cout << edge.first << ' ' << edge.second << '\n';
        }
    }

    return 0;
}

