#include <bits/stdc++.h>
using namespace std;

struct Fenwick {
    int size;
    vector<int> tree;

    Fenwick(int n) : size(n), tree(n + 1) {}

    void update(int idx, int delta) {
        for (++idx; idx <= size; idx += idx & -idx)
            tree[idx] += delta;
    }

    int query(int idx) {
        int res = 0;
        for (++idx; idx > 0; idx -= idx & -idx)
            res += tree[idx];
        return res;
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    int k = n * m;
    vector<vector<int>> grid(n, vector<int>(m));
    vector<pair<int, int>> pos(k + 1); // f -> (x, y)
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            cin >> grid[i][j];
            pos[grid[i][j]] = {i, j};
        }
    }

    vector<vector<int>> adj(k + 1);
    vector<int> dirx = {0, 0, 1, -1};
    vector<int> diry = {1, -1, 0, 0};
    for (int f = 1; f <= k; ++f) {
        auto [x, y] = pos[f];
        for (int d = 0; d < 4; ++d) {
            int nx = x + dirx[d];
            int ny = y + diry[d];
            if (nx >= 0 && nx < n && ny >= 0 && ny < m) {
                int neighbor_f = grid[nx][ny];
                if (neighbor_f < f) {
                    adj[f].push_back(neighbor_f);
                }
            }
        }
    }

    Fenwick fen(k);
    unordered_map<int, int> cnt_map;
    cnt_map[0 - 1] = 1; // prefix_sum[0-1] - 1 = 0-1
    long long ans = 0;

    for (int r = 1; r <= k; ++r) {
        for (int i : adj[r]) {
            fen.update(i, 1);
        }

        int total_edges_r = fen.query(r - 1);
        int target = total_edges_r - r;

        ans += cnt_map[target];

        int prefix_sum_r = fen.query(r);
        int key = prefix_sum_r - (r + 1);
        cnt_map[key]++;
    }

    cout << ans << endl;

    return 0;
}

