#include <bits/stdc++.h>
using namespace std;

struct Edge {
    int to, rev;
    int cap, flow;
};

class MaxFlow {
public:
    vector<vector<Edge>> g;
    vector<int> level, ptr;

    MaxFlow(int n) : g(n), level(n), ptr(n) {}

    void add_edge(int from, int to, int cap) {
        Edge e1 = {to, (int)g[to].size(), cap, 0};
        Edge e2 = {from, (int)g[from].size(), 0, 0};
        g[from].push_back(e1);
        g[to].push_back(e2);
    }

    bool bfs(int s, int t) {
        fill(level.begin(), level.end(), -1);
        queue<int> q;
        level[s] = 0;
        q.push(s);
        while (!q.empty()) {
            int v = q.front();
            q.pop();
            for (Edge &e : g[v]) {
                if (e.cap > e.flow && level[e.to] == -1) {
                    level[e.to] = level[v] + 1;
                    q.push(e.to);
                }
            }
        }
        return level[t] != -1;
    }

    int dfs(int v, int t, int flow) {
        if (v == t || flow == 0)
            return flow;
        for (int &i = ptr[v]; i < g[v].size(); ++i) {
            Edge &e = g[v][i];
            if (level[e.to] == level[v] + 1) {
                int pushed = dfs(e.to, t, min(flow, e.cap - e.flow));
                if (pushed > 0) {
                    e.flow += pushed;
                    g[e.to][e.rev].flow -= pushed;
                    return pushed;
                }
            }
        }
        return 0;
    }

    int max_flow(int s, int t) {
        int flow = 0;
        while (bfs(s, t)) {
            fill(ptr.begin(), ptr.end(), 0);
            while (int pushed = dfs(s, t, INT_MAX))
                flow += pushed;
        }
        return flow;
    }
};

struct SegNode {
    int l, r;
    int left = -1, right = -1;
};

vector<SegNode> seg_nodes;

int build_segment_tree(int l, int r, int &id) {
    int node_id = seg_nodes.size();
    seg_nodes.push_back({l, r, -1, -1});
    if (l == r) {
        return node_id;
    } else {
        int mid = (l + r) / 2;
        int left = build_segment_tree(l, mid, id);
        int right = build_segment_tree(mid+1, r, id);
        seg_nodes[node_id].left = left;
        seg_nodes[node_id].right = right;
        return node_id;
    }
}

void get_seg_nodes(int node_id, int l, int r, vector<int> &res) {
    SegNode &node = seg_nodes[node_id];
    if (node.r < l || node.l > r)
        return;
    if (l <= node.l && node.r <= r) {
        res.push_back(node_id);
        return;
    }
    get_seg_nodes(node.left, l, r, res);
    get_seg_nodes(node.right, l, r, res);
}

int find_ship(int seg_node, MaxFlow &mf) {
    int current = seg_node;
    while (true) {
        SegNode &node = seg_nodes[current];
        if (node.l == node.r) {
            return node.l;
        }
        bool found = false;
        for (Edge &e : mf.g[current]) {
            if (e.flow > 0 && (e.to == node.left || e.to == node.right)) {
                current = e.to;
                found = true;
                break;
            }
        }
        if (!found) {
            return -1;
        }
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);

    int N, M;
    cin >> N >> M;

    vector<int> weapon_type(N);
    vector<vector<int>> sql_ships(N);
    vector<pair<int, int>> cogn_interval(N);
    vector<tuple<int, int, int>> omg_ships(N);

    seg_nodes.clear();
    int seg_id = 0;
    int root = build_segment_tree(1, M, seg_id);
    int seg_count = seg_nodes.size();

    for (int i = 0; i < N; ++i) {
        int type;
        cin >> type;
        weapon_type[i] = type;
        if (type == 0) {
            int K;
            cin >> K;
            vector<int> ships(K);
            for (int j = 0; j < K; ++j) {
                cin >> ships[j];
            }
            sql_ships[i] = ships;
        } else if (type == 1) {
            int l, r;
            cin >> l >> r;
            cogn_interval[i] = {l, r};
        } else if (type == 2) {
            int a, b, c;
            cin >> a >> b >> c;
            omg_ships[i] = {a, b, c};
        }
    }

    int total_nodes = 2 + N + seg_count + M;
    MaxFlow mf(total_nodes);
    const int source = 0;
    const int sink = 1;

    for (int i = 0; i < seg_count; ++i) {
        SegNode &node = seg_nodes[i];
        if (node.l == node.r) {
            int ship_id = 2 + N + seg_count + (node.l - 1);
            mf.add_edge(2 + N + i, ship_id, 1);
        } else {
            mf.add_edge(2 + N + i, 2 + N + node.left, 1);
            mf.add_edge(2 + N + i, 2 + N + node.right, 1);
        }
    }

    for (int i = 0; i < N; ++i) {
        int weapon_node = 2 + i;
        if (weapon_type[i] == 0) {
            mf.add_edge(source, weapon_node, 1);
            for (int ship : sql_ships[i]) {
                int ship_id = 2 + N + seg_count + (ship - 1);
                mf.add_edge(weapon_node, ship_id, 1);
            }
        } else if (weapon_type[i] == 1) {
            mf.add_edge(source, weapon_node, 1);
            int l = cogn_interval[i].first;
            int r = cogn_interval[i].second;
            vector<int> nodes;
            get_seg_nodes(root, l, r, nodes);
            for (int seg_node : nodes) {
                mf.add_edge(weapon_node, 2 + N + seg_node, 1);
            }
        } else if (weapon_type[i] == 2) {
            auto [a, b, c] = omg_ships[i];
            mf.add_edge(source, weapon_node, 2);
            int a_id = 2 + N + seg_count + (a - 1);
            int b_id = 2 + N + seg_count + (b - 1);
            int c_id = 2 + N + seg_count + (c - 1);
            mf.add_edge(weapon_node, a_id, 1);
            mf.add_edge(weapon_node, b_id, 1);
            mf.add_edge(weapon_node, c_id, 1);
        }
    }

    for (int i = 1; i <= M; ++i) {
        int ship_id = 2 + N + seg_count + (i - 1);
        mf.add_edge(ship_id, sink, 1);
    }

    int max_flow = mf.max_flow(source, sink);
    cout << max_flow << "\n";

    vector<pair<int, int>> result;

    for (int i = 0; i < N; ++i) {
        int weapon_node = 2 + i;
        int type = weapon_type[i];
        if (type == 0) {
            for (Edge &e : mf.g[weapon_node]) {
                if (e.to == source) continue;
                if (e.flow == 1) {
                    int ship = (e.to - (2 + N + seg_count)) + 1;
                    result.emplace_back(i+1, ship);
                    break;
                }
            }
        } else if (type == 1) {
            for (Edge &e : mf.g[weapon_node]) {
                if (e.to == source) continue;
                if (e.flow == 1) {
                    int seg_node_id = e.to - (2 + N);
                    int ship = find_ship(seg_node_id, mf);
                    if (ship != -1) {
                        result.emplace_back(i+1, ship);
                    }
                    break;
                }
            }
        } else if (type == 2) {
            vector<int> ships;
            for (Edge &e : mf.g[weapon_node]) {
                if (e.flow > 0) {
                    int ship = (e.to - (2 + N + seg_count)) + 1;
                    ships.push_back(ship);
                }
            }
            if (ships.size() == 2) {
                result.emplace_back(i+1, ships[0]);
                result.emplace_back(i+1, ships[1]);
            }
        }
    }

    for (auto [a, b] : result) {
        cout << a << " " << b << "\n";
    }

    return 0;
}

