    Edge(int u, int v, int idx) : u(u), v(v), idx(idx) {}
};

vector<vector<Edge>> adj;
vector<int> parent, depth, up_edge_idx;

void dfs(int u, int p, int e_idx) {
    parent[u] = p;
    for (auto& e : adj[u]) {
        if (e.v != p) {
            depth[e.v] = depth[u] + 1;
            up_edge_idx[e.v] = e.idx;
            dfs(e.v, u, e.idx);
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, k;
    cin >> n >> k;

    vector<int> a(k);
    for (int i = 0; i < k; i++) {
        cin >> a[i];
        a[i]--;
    }

    vector<Edge> edges;
    adj.resize(n);
    for (int i = 0; i < n-1; i++) {
        int u, v;
        cin >> u >> v;
        u--; v--;
        edges.emplace_back(u, v, i);
        adj[u].emplace_back(u, v, i);
        adj[v].emplace_back(v, u, i);
    }

    parent.assign(n, -1);
    depth.assign(n, 0);
    up_edge_idx.assign(n, -1);
    dfs(0, -1, -1);

    vector<int> edge_min(k, -1);
    for (int i = 0; i < k; i++) {
        int u = a[i];
        int min_idx = n;
        while (u != 0) {
            min_idx = min(min_idx, up_edge_idx[u]);
            u = parent[u];
        }
        edge_min[i] = (min_idx == n ? -1 : min_idx);
    }

    map<int, int> cnt;
    for (int x : edge_min) cnt[x]++;

    ll ans = 0;
    for (auto [e_idx, c] : cnt) {
        ll s = c;
        ll t = k - c;
        ans = (ans + s * t % MOD) % MOD;
    }

    ans = ans * INV2 % MOD;

    ll denom = (ll)k * (k-1) / 2 % MOD;
    ans = ans * (denom ? MOD - denom + 1 : 0) % MOD;
    ans = ans * 748683265LL % MOD;

    cout << ans << '\n';

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int MOD = 998244353;

struct DSU {
    vector<int> parent;
    vector<bool> is_parent;
    vector<int> children;

    DSU(int n) : parent(n+1), is_parent(n+1, false), children(n+1, 0) {
        for(int i = 1; i <= n; ++i) {
            parent[i] = i;
        }
    }

    int find(int u) {
        if (parent[u] == u) return u;
        return parent[u] = find(parent[u]);
    }

    bool unite(int u, int v, int& parent_node, int& child_node) {
        u = find(u);
        v = find(v);
        if (u == v) return false;

        if (!is_parent[u] && !is_parent[v]) {
            parent[v] = u;
            is_parent[u] = true;
            parent_node = u;
            child_node = v;
        } else if (is_parent[u] && is_parent[v]) {
            return false;
        } else {
            if (is_parent[v]) swap(u, v);
            parent[v] = u;
            parent_node = u;
            child_node = v;
        }
        return true;
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    vector<pair<int, int>> edges;
    for(int i = 0; i < n-1; ++i) {
        int u, v;
        cin >> u >> v;
        edges.emplace_back(u, v);
    }

    DSU dsu(n);
    vector<int> children(n+1, 0);

    for(auto& [u, v] : edges) {
        int parent_node, child_node;
        if (dsu.unite(u, v, parent_node, child_node)) {
            children[parent_node]++;
        }
    }

    long long result = 1;
    for(int i = 1; i <= n; ++i) {
        result = result * (children[i] + 1) % MOD;
    }

    cout << result << endl;

    return 0;
}

