#include <bits/stdc++.h>
using namespace std;

struct SegmentTree {
    int n;
    vector<long long> tree;
    vector<long long> lazy;

    SegmentTree(int size, const vector<long long>& arr) {
        n = size;
        tree.resize(4 * n);
        lazy.resize(4 * n, 0);
        build(1, 0, n-1, arr);
    }

    void build(int node, int start, int end, const vector<long long>& arr) {
        if (start == end) {
            tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            build(2*node, start, mid, arr);
            build(2*node+1, mid+1, end, arr);
            tree[node] = min(tree[2*node], tree[2*node+1]);
        }
    }

    void push(int node) {
        if (lazy[node] != 0) {
            tree[2*node] += lazy[node];
            tree[2*node+1] += lazy[node];
            lazy[2*node] += lazy[node];
            lazy[2*node+1] += lazy[node];
            lazy[node] = 0;
        }
    }

    void range_add(int l, int r, long long val) {
        range_add(1, 0, n-1, l, r, val);
    }

    void range_add(int node, int start, int end, int l, int r, long long val) {
        if (r < start || end < l) return;
        if (l <= start && end <= r) {
            tree[node] += val;
            lazy[node] += val;
            return;
        }
        push(node);
        int mid = (start + end) / 2;
        range_add(2*node, start, mid, l, r, val);
        range_add(2*node+1, mid+1, end, l, r, val);
        tree[node] = min(tree[2*node], tree[2*node+1]);
    }

    long long range_min(int l, int r) {
        return range_min(1, 0, n-1, l, r);
    }

    long long range_min(int node, int start, int end, int l, int r) {
        if (r < start || end < l) return LLONG_MAX;
        if (l <= start && end <= r) return tree[node];
        push(node);
        int mid = (start + end)/2;
        return min(range_min(2*node, start, mid, l, r),
                   range_min(2*node+1, mid+1, end, l, r));
    }
};

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    vector<int> p(n), a(n);
    for (int i = 0; i < n; ++i) {
        cin >> p[i];
    }
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    vector<long long> S(n);
    S[0] = a[0];
    for (int i = 1; i < n; ++i) {
        S[i] = S[i-1] + a[i];
    }

    vector<tuple<int, int, int>> elements;
    for (int i = 0; i < n; ++i) {
        elements.emplace_back(p[i], a[i], i+1);
    }
    sort(elements.begin(), elements.end());

    vector<long long> arr(n);
    for (int i = 0; i < n; ++i) {
        arr[i] = S[i];
    }
    SegmentTree st(n, arr);

    long long min_cost = LLONG_MAX;

    if (n > 1) {
        long long current_min = st.range_min(0, n-2);
        min_cost = current_min;
    }

    long long sum_lex_total = 0;

    for (auto& elem : elements) {
        int a_val = get<1>(elem);
        int pos = get<2>(elem);

        sum_lex_total += a_val;
        st.range_add(pos-1, n-1, -2 * a_val);

        if (n == 1) continue;

        long long current_min = st.range_min(0, n-2);
        long long current_cost = sum_lex_total + current_min;

        if (current_cost < min_cost) {
            min_cost = current_cost;
        }
    }

    cout << min_cost << '\n';

    return 0;
}

