using namespace std;

vector<vector<pair<int, int>>> adj;
vector<int> parent;
vector<vector<int>> children;
vector<int> depth;

void build_tree(int s, int n) {
    parent.assign(n+1, -1);
    children.assign(n+1, vector<int>());
    depth.assign(n+1, 0);
    queue<int> q;
    q.push(s);
    parent[s] = -1;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (auto &edge : adj[u]) {
            int v = edge.first, w = edge.second;
            if (parent[v] == -1 && v != parent[u]) {
                parent[v] = u;
                depth[v] = depth[u] + w;
                children[u].push_back(v);
                q.push(v);
            }
        }
    }
}

int compute_max_depth(int u) {
    int max_d = depth[u];
    for (int v : children[u]) {
        max_d = max(max_d, compute_max_depth(v));
    }
    return max_d;
}

int main() {
    int n;
    cin >> n;
    adj.resize(n+1);
    for (int i = 0; i < n-1; ++i) {
        int u, v, w;
        cin >> u >> v >> w;
        adj[u].emplace_back(v, w);
        adj[v].emplace_back(u, w);
    }
    int s, m;
    cin >> s >> m;
    vector<int> criminals(m);
    for (int i = 0; i < m; ++i) {
        cin >> criminals[i];
    }

    build_tree(s, n);

    map<int, int> child_depths; // child of s to max depth in subtree
    for (int child : children[s]) {
        int max_d = compute_max_depth(child);
        child_depths[child] = max_d;
    }

    // For each criminal, find which child of s's subtree it belongs to
    vector<int> depths;
    for (int x : criminals) {
        int current = x;
        while (parent[current] != s && parent[current] != -1) {
            current = parent[current];
        }
        if (parent[current] == s) {
            depths.push_back(child_depths[current]);
        } else if (current == s) {
            // Criminal is at s, but input says xj != s, so this shouldn't happen
        } else {
            // This criminal is in the subtree of s but not in any child (possible if tree is rooted at s and criminal is s's parent)
            // But since s is the root, this can't happen as per build_tree
        }
    }

    if (depths.empty()) {
        cout << "Terrorists win" << endl;
        return 0;
    }

    int sum_d = 0;
    int max_d = 0;
    for (int d : depths) {
        sum_d += d;
        max_d = max(max_d, d);
    }
    int time = 2 * sum_d - max_d;
    cout << time << endl;

    return 0;
}

