#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

const int MAXN = 2e5 + 5;

int mu[MAXN];
vector<int> divisors[MAXN];

void sieve_mobius(int n) {
    vector<int> spf(n+1, 0);
    mu[1] = 1;
    for (int i = 2; i <= n; ++i) {
        if (!spf[i]) {
            spf[i] = i;
            for (int j = i; j <= n; j += i) {
                if (!spf[j]) spf[j] = i;
            }
        }
        int p = spf[i];
        int j = i / p;
        if (spf[j] == p) mu[i] = 0;
        else mu[i] = -mu[j];
    }
}

void compute_divisors(int n) {
    for (int i = 1; i <= n; ++i) {
        for (int j = i; j <= n; j += i) {
            divisors[j].push_back(i);
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(0);

    int n;
    cin >> n;
    vector<ll> a(n+1), b(n+1);
    for (int i = 1; i <= n; ++i) cin >> a[i];
    for (int i = 1; i <= n; ++i) {
        cin >> b[i];
        if (i == 1) b[i] = -1;
    }

    sieve_mobius(n);
    compute_divisors(n);

    vector<ll> D(n+1);
    for (int i = 2; i <= n; ++i) {
        D[i] = b[i] - a[i];
    }

    vector<ll> c(n+1, 0);
    for (int i = 1; i <= n; ++i) {
        for (int d : divisors[i]) {
            if (d == i) continue;
            int j = i / d;
            if (j >= 2) {
                c[i] += mu[d] * D[j];
            }
        }
    }

    vector<ll> crit;
    ll sum_fixed = 0;
    int a1 = a[1];
    for (int i = 1; i <= n; ++i) {
        int ai = mu[i];
        if (ai == 1) {
            crit.push_back(a1 - c[i]);
        } else if (ai == -1) {
            crit.push_back(a1 + c[i]);
        } else {
            sum_fixed += abs(c[i]);
        }
    }

    sort(crit.begin(), crit.end());
    int m = crit.size();
    vector<ll> prefix(m+1, 0);
    for (int i = 0; i < m; ++i) {
        prefix[i+1] = prefix[i] + crit[i];
    }
    ll sum_total = prefix[m];

    int q;
    cin >> q;
    while (q--) {
        ll x;
        cin >> x;
        int pos = upper_bound(crit.begin(), crit.end(), x) - crit.begin();
        ll sum_abs = x * pos - prefix[pos];
        sum_abs += (sum_total - prefix[pos]) - x * (m - pos);
        cout << sum_abs + sum_fixed << '\n';
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

vector<string> ask(int l, int r) {
    cout << "? " << l << " " << r << endl;
    int cnt = (r - l + 1) * (r - l + 2) / 2;
    vector<string> res;
    for (int i = 0; i < cnt; ++i) {
        string s;
        cin >> s;
        sort(s.begin(), s.end());
        res.push_back(s);
    }
    return res;
}

void guess(string s) {
    cout << "! " << s << endl;
}

int main() {
    int n;
    cin >> n;

    if (n == 1) {
        auto res = ask(1, 1);
        guess(res[0]);
        return 0;
    }

    auto all = ask(1, n);
    map<char, int> cnt;
    for (auto& s : all)
        if (s.size() == 1)
            cnt[s[0]]++;

    char first, last;
    auto first_part = ask(1, 1);
    first = first_part[0][0];
    auto last_part = ask(n, n);
    last = last_part[0][0];

    cnt[first]--;
    cnt[last]--;

    string s(n, ' ');
    s[0] = first;
    s[n-1] = last;

    if (n == 2) {
        guess(s);
        return 0;
    }

    vector<char> mid;
    for (auto& [c, count] : cnt)
        while (count-- > 0)
            mid.push_back(c);

    vector<map<char, int>> parts(3);
    int split = (n-2 + 2)/3;
    int l1 = 2, r1 = 1 + split;
    int l2 = r1 + 1, r2 = r1 + split;
    int l3 = r2 + 1, r3 = n-1;

    if (r1 > n-1) r1 = n-1;
    if (r2 > n-1) r2 = n-1;

    auto part1 = ask(l1, r1);
    for (auto& s : part1)
        for (char c : s)
            parts[0][c]++;

    auto part2 = ask(l2 > n-1 ? n-1 : l2, r2 > n-1 ? n-1 : r2);
    for (auto& s : part2)
        for (char c : s)
            parts[1][c]++;

    auto part3 = ask(l3 > n-1 ? n-1 : l3, r3);
    for (auto& s : part3)
        for (char c : s)
            parts[2][c]++;

    vector<int> len = {r1 - l1 + 1, r2 - l2 + 1, r3 - l3 + 1};
    vector<string> built(3);

    for (int i = 0; i < 3; ++i) {
        if (len[i] == 0) continue;
        map<char, int> freq;
        for (auto& [c, count] : parts[i])
            if (count % (len[i] - (i == 2 ? 0 : 1) + 1) == 0)
                freq[c] = count / (len[i] - (i == 2 ? 0 : 1) + 1);
            else
                freq[c] = count / ((len[i] * (len[i] + 1)) / 2);

        string tmp;
        for (auto& c : mid) {
            if (freq[c] > 0) {
                tmp += c;
                freq[c]--;
            }
        }
        built[i] = tmp.substr(0, len[i]);
    }

    string mid_str;
    for (auto& s : built)
        mid_str += s;

    for (int i = 1; i < n-1; ++i)
        s[i] = mid_str[i-1];

    guess(s);
    return 0;
}

