    edges.emplace_back(3, 6);
    edges.emplace_back(4, 7);
    edges.emplace_back(4, 8);
    int next = 9;
    int parent = 5;
    while (next <= n) {
        edges.emplace_back(parent, next);
        next++;
        if (next <= n) {
            edges.emplace_back(parent, next);
            next++;
        }
        parent++;
