#include <bits/stdc++.h>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m, q;
    cin >> n >> m >> q;
    vector<int> p(n);
    for (int &x : p) cin >> x;

    if (n == 1) {
        string ans(q, '1');
        while (q--) { int l, r; cin >> l >> r; }
        cout << ans << '\n';
        return 0;
    }

    vector<int> succ(n + 1);
    for (int i = 0; i < n; ++i) {
        int current = p[i];
        int next_val = p[(i + 1) % n];
        succ[current] = next_val;
    }

    vector<int> a(m);
    for (int &x : a) cin >> x;

    const int m_val = m;
    vector<int> next_arr(m, m_val);
    vector<int> last_occurrence(n + 1, m_val);
    for (int i = m - 1; i >= 0; --i) {
        int s = succ[a[i]];
        if (last_occurrence[s] != m_val)
            next_arr[i] = last_occurrence[s];
        last_occurrence[a[i]] = i;
    }

    const int levels = 20;
    vector<vector<int>> lift(levels, vector<int>(m, m_val));
    lift[0] = next_arr;
    for (int j = 1; j < levels; ++j) {
        for (int i = 0; i < m; ++i) {
            if (lift[j-1][i] != m_val)
                lift[j][i] = lift[j-1][lift[j-1][i]];
        }
    }

    vector<int> f(m, m_val);
    for (int i = 0; i < m; ++i) {
        int pos = i;
        int steps = n - 1;
        for (int j = 0; j < levels; ++j) {
            if ((steps & (1 << j)) && pos != m_val)
                pos = lift[j][pos];
        }
        f[i] = pos;
    }

    vector<int> log_table(m + 1);
    for (int i = 2; i <= m; ++i)
        log_table[i] = log_table[i/2] + 1;

    int max_level = log_table[m] + 1;
    vector<vector<int>> st(max_level, vector<int>(m));
    st[0] = f;
    for (int j = 1; j < max_level; ++j) {
        for (int i = 0; i + (1 << j) <= m; ++i) {
            st[j][i] = min(st[j-1][i], st[j-1][i + (1 << (j-1))]);
        }
    }

    string ans;
    while (q--) {
        int l, r;
        cin >> l >> r;
        l--; r--;
        if (r - l + 1 < n) {
            ans += '0';
            continue;
        }
        int len = r - l + 1;
        int k = log_table[len];
        int min_val = min(st[k][l], st[k][r - (1 << k) + 1]);
        ans += (min_val <= r) ? '1' : '0';
    }

    cout << ans << '\n';
    return 0;
}

