    prefix_aC.resize(n+1, 0);
    for (int i = 0; i < n; ++i) {
        prefix_aA[i+1] = prefix_aA[i] + (a[i] == 'A');
        prefix_aT[i+1] = prefix_aT[i] + (a[i] == 'T');
        prefix_aC[i+1] = prefix_aC[i] + (a[i] == 'C');
    }

    prefix_bA.resize(n+1, 0);
    prefix_bT.resize(n+1, 0);
    prefix_bC.resize(n+1, 0);
    for (int i = 0; i < n; ++i) {
        prefix_bA[i+1] = prefix_bA[i] + (b[i] == 'A');
        prefix_bT[i+1] = prefix_bT[i] + (b[i] == 'T');
        prefix_bC[i+1] = prefix_bC[i] + (b[i] == 'C');
    }

    prefix_AT.resize(n+1, 0);
    prefix_AC.resize(n+1, 0);
    prefix_TA.resize(n+1, 0);
    prefix_TC.resize(n+1, 0);
    prefix_CA.resize(n+1, 0);
    prefix_CT.resize(n+1, 0);
    for (int i = 0; i < n; ++i) {
        prefix_AT[i+1] = prefix_AT[i] + (a[i] == 'A' && b[i] == 'T');
        prefix_AC[i+1] = prefix_AC[i] + (a[i] == 'A' && b[i] == 'C');
        prefix_TA[i+1] = prefix_TA[i] + (a[i] == 'T' && b[i] == 'A');
        prefix_TC[i+1] = prefix_TC[i] + (a[i] == 'T' && b[i] == 'C');
        prefix_CA[i+1] = prefix_CA[i] + (a[i] == 'C' && b[i] == 'A');
        prefix_CT[i+1] = prefix_CT[i] + (a[i] == 'C' && b[i] == 'T');
    }
}

int get_distance(int x, int y) {
    int aA = prefix_aA[y+1] - prefix_aA[x];
    int aT = prefix_aT[y+1] - prefix_aT[x];
    int aC = prefix_aC[y+1] - prefix_aC[x];

    int bA = prefix_bA[y+1] - prefix_bA[x];
    int bT = prefix_bT[y+1] - prefix_bT[x];
    int bC = prefix_bC[y+1] - prefix_bC[x];

    if (aA != bA || aT != bT || aC != bC) return -1;

    int count_AT = prefix_AT[y+1] - prefix_AT[x];
    int count_AC = prefix_AC[y+1] - prefix_AC[x];
    int count_TA = prefix_TA[y+1] - prefix_TA[x];
    int count_TC = prefix_TC[y+1] - prefix_TC[x];
    int count_CA = prefix_CA[y+1] - prefix_CA[x];
    int count_CT = prefix_CT[y+1] - prefix_CT[x];

    int swaps = 0;

    int min_val;

    min_val = min(count_AT, count_TA);
    swaps += min_val;
    count_AT -= min_val;
    count_TA -= min_val;

    min_val = min(count_AC, count_CA);
    swaps += min_val;
    count_AC -= min_val;
    count_CA -= min_val;

    min_val = min(count_TC, count_CT);
    swaps += min_val;
    count_TC -= min_val;
    count_CT -= min_val;

    min_val = min({count_AT, count_TC, count_CA});
    swaps += 2 * min_val;
    count_AT -= min_val;
    count_TC -= min_val;
    count_CA -= min_val;

    min_val = min({count_AC, count_CT, count_TA});
    swaps += 2 * min_val;
    count_AC -= min_val;
    count_CT -= min_val;
    count_TA -= min_val;

    return swaps;
}

