#include <bits/stdc++.h>
using namespace std;

const int mod = 998244353;

int powmod(int a, int b) {
    int res = 1;
    while (b > 0) {
        if (b & 1) {
            res = (long long) res * a % mod;
        }
        a = (long long) a * a % mod;
        b >>= 1;
    }
    return res;
}

void ntt(vector<int>& a, bool invert) {
    int n = a.size();
    int log_n = 0;
    while ((1 << log_n) < n) log_n++;
    vector<int> rev(n);
    for (int i = 0; i < n; i++) {
        rev[i] = (rev[i >> 1] >> 1) | ((i & 1) << (log_n - 1));
        if (i < rev[i]) swap(a[i], a[rev[i]]);
    }
    for (int len = 2; len <= n; len <<= 1) {
        int wlen = invert ? powmod(3, mod - 1 - (mod - 1) / len) : powmod(3, (mod - 1) / len);
        for (int i = 0; i < n; i += len) {
            int w = 1;
            for (int j = 0; j < len / 2; j++) {
                int u = a[i + j], v = (long long)a[i + j + len/2] * w % mod;
                a[i + j] = (u + v) % mod;
                a[i + j + len/2] = (u - v + mod) % mod;
                w = (long long)w * wlen % mod;
            }
        }
    }
    if (invert) {
        int inv_n = powmod(n, mod - 2);
        for (int& x : a) x = (long long)x * inv_n % mod;
    }
}

vector<int> multiply(vector<int> a, vector<int> b) {
    int sz = a.size() + b.size() - 1;
    int n = 1;
    while (n < sz) n <<= 1;
    a.resize(n), b.resize(n);
    ntt(a, false), ntt(b, false);
    for (int i = 0; i < n; i++) a[i] = (long long)a[i] * b[i] % mod;
    ntt(a, true);
    a.resize(sz);
    return a;
}

vector<int> power_poly(vector<int> a, int e) {
    vector<int> res = {1};
    while (e > 0) {
        if (e & 1) res = multiply(res, a);
        a = multiply(a, a);
        e >>= 1;
    }
    return res;
}

