#include <bits/stdc++.h>
#include <vector>
#include <queue>
#include <unordered_set>
#include "islands.h"

using namespace std;

struct State {
    int current;
    int mask;
    int last;

    bool operator==(const State& other) const {
        return current == other.current && mask == other.mask && last == other.last;
    }
};

namespace std {
    template<> struct hash<State> {
        size_t operator()(const State& s) const {
            size_t h1 = hash<int>()(s.current);
            size_t h2 = hash<int>()(s.mask);
            size_t h3 = hash<int>()(s.last);
            return (h1 << 24) ^ (h2 << 4) ^ h3;
        }
    };
}

variant<bool, vector<int>> find_journey(int N, int M, vector<int> U, vector<int> V) {
    queue<pair<State, vector<int>>> q;
    unordered_set<State> visited;

    State initial = {0, 0, -1};
    q.push({initial, {}});
    visited.insert(initial);

    while (!q.empty()) {
        auto [state, path] = q.front();
        q.pop();

        if (state.current == 0 && state.mask == 0 && path.size() >= 2) {
            return path;
        }

        for (int i = 0; i < M; ++i) {
            if (i == state.last) continue;

            int dock;
            if ((state.mask & (1 << i)) == 0) {
                dock = U[i];
            } else {
                dock = V[i];
            }

            if (dock != state.current) continue;

            int new_mask = state.mask ^ (1 << i);
            int new_current = (U[i] == state.current) ? V[i] : U[i];
            State new_state = {new_current, new_mask, i};

            if (visited.find(new_state) == visited.end()) {
                vector<int> new_path = path;
                new_path.push_back(i);
                visited.insert(new_state);
                q.push({new_state, new_path});
            }
        }
    }

    return false;
}

