    cin >> n >> s;
    vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        a[i] = s[i] - '0';
    }

    int T = 0;
    for (int i = 0; i < n - 1; ++i) {
        if (a[i] != a[i+1]) ++T;
    }

    vector<int> gain_left(n, 0);
    for (int i = 1; i < n; ++i) {
        bool original = (a[i-1] != a[i]);
        bool flipped = (a[i-1] != (1 - a[i]));
        gain_left[i] = flipped - original;
    }

    vector<int> gain_right(n, 0);
    for (int j = 0; j < n-1; ++j) {
        bool original = (a[j] != a[j+1]);
        bool flipped = ((1 - a[j]) != a[j+1]);
        gain_right[j] = flipped - original;
    }

    vector<int> suffix_max(n);
    suffix_max[n-1] = gain_right[n-1];
    for (int j = n-2; j >= 0; --j) {
        suffix_max[j] = max(gain_right[j], suffix_max[j+1]);
    }

    int max_delta = 0;
    for (int i = 0; i < n; ++i) {
        int current = gain_left[i] + (i < n ? suffix_max[i] : 0);
        max_delta = max(max_delta, current);
    }

    cout << (T + max_delta + 1) << endl;
    return 0;
}

