                cycle.push_back(current);
                current = pos[current - 1];
            }
            groups.push_back(cycle);
        }
    }
    cout << groups.size() << '\n';
    for (const auto& group : groups) {
        cout << group.size();
        for (int idx : group) {
            cout << ' ' << idx;
        }
        cout << '\n';
    }
    return 0;
}

