using namespace std;

struct Node {
    Node* children[2];
    bool is_code;

    Node() : children{nullptr, nullptr}, is_code(false) {}
};

bool generate_codes(const vector<int>& sorted_lengths, vector<string>& codes) {
    Node root;

    for (int l : sorted_lengths) {
        Node* current = &root;
        string code(l, '0');
        bool valid = true;

        for (int i = 0; i < l; ++i) {
            bool found = false;
            for (int bit = 0; bit < 2; ++bit) {
                if (current->children[bit]) {
                    if (current->children[bit]->is_code) {
                        continue;
                    }
                    current = current->children[bit];
                    code[i] = '0' + bit;
                    found = true;
                    break;
                } else {
                    current->children[bit] = new Node();
                    current = current->children[bit];
                    code[i] = '0' + bit;
                    found = true;
                    break;
                }
            }

            if (!found) {
                valid = false;
                break;
            }
        }

        if (!valid) {
            return false;
        }

        current->is_code = true;
        codes.push_back(code);
    }

    return true;
}

