void solve() {
    int n;
    cin >> n;
    vector<int> a(n);
    for (auto &x : a) cin >> x;
    sort(a.begin(), a.end());

    // Compute prefix_b: sum (k - a[k]) for k from 0 to i-1
    vector<long long> prefix_b(n + 1, 0);
    for (int i = 1; i <= n; ++i) {
        prefix_b[i] = prefix_b[i - 1] + (i - 1 - a[i - 1]);
    }

    // Find first_k where a[k] >k
    int first_k = n;
    int low = 0, high = n - 1;
    while (low <= high) {
        int mid = (low + high) / 2;
        if (a[mid] > mid) {
            first_k = mid;
            high = mid - 1;
        } else {
            low = mid + 1;
        }
    }

    // Precompute count_0
    int count_0 = upper_bound(a.begin(), a.end(), 0) - lower_bound(a.begin(), a.end(), 0);

    for (int i = 0; i <= n; ++i) {
        if (i == 0) {
            cout << count_0 << ' ';
            continue;
        }
        if (i > first_k || i > n) {
            cout << "-1 ";
            continue;
        }
        long long sum_b = prefix_b[i];
        // Compute sum_c: number of elements ==i in a[j] >=i
        auto left = lower_bound(a.begin(), a.end(), i) - a.begin();
        left = max(left, i);
        auto right = upper_bound(a.begin(), a.end(), i) - a.begin();
        int sum_c = max(0, (int)(right - left));
        cout << sum_b + sum_c << ' ';
    }
    cout << '\n';
}

