#include <bits/stdc++.h>
using namespace std;

const int MAXN = 4e5 + 10;

vector<int> adj[MAXN];
int color[MAXN], parent[MAXN], sz[MAXN];
long long sum[MAXN];
unordered_map<int, vector<int>> color_nodes;
long long total;
int n, m;

int find(int u) {
    if (parent[u] == u) return u;
    return parent[u] = find(parent[u]);
}

void unite(int u, int v) {
    u = find(u), v = find(v);
    if (u == v) return;
    if (sz[u] < sz[v]) swap(u, v);
    sum[color[u]] -= (long long)sz[u] * sz[u];
    sum[color[v]] -= (long long)sz[v] * sz[v];
    sz[u] += sz[v];
    sum[color[u]] += (long long)sz[u] * sz[u];
    parent[v] = u;
}

void update_color(int u, int new_c) {
    int old_c = color[u];
    if (old_c == new_c) return;

    total -= (n * 1LL * n - sum[old_c]);
    total -= (n * 1LL * n - sum[new_c]);

    sum[old_c] -= sz[u] * sz[u];
    sz[u] = 1;
    sum[old_c] += sz[u] * sz[u];
    parent[u] = u;
    for (int v : adj[u]) {
        if (color[v] != old_c) continue;
        unite(u, v);
    }

    sum[new_c] -= sz[u] * sz[u];
    sz[u] = 1;
    sum[new_c] += sz[u] * sz[u];
    parent[u] = u;
    for (int v : adj[u]) {
        if (color[v] != new_c) continue;
        unite(u, v);
    }

    color[u] = new_c;

    total += (n * 1LL * n - sum[old_c]);
    total += (n * 1LL * n - sum[new_c]);
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> m;
    for (int i = 1; i <= n; ++i) {
        cin >> color[i];
        color_nodes[color[i]].push_back(i);
    }

    for (int i = 0; i < n-1; ++i) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    fill(sz + 1, sz + n + 1, 1);
    for (int i = 1; i <= n; ++i) {
        parent[i] = i;
    }

    for (auto &[c, nodes] : color_nodes) {
        for (int u : nodes) {
            for (int v : adj[u]) {
                if (color[v] == c) {
                    unite(u, v);
                }
            }
        }
    }

    for (int c = 1; c <= n; ++c) {
        sum[c] = n * 1LL * n;
    }

    for (auto &[c, nodes] : color_nodes) {
        sum[c] = 0;
        for (int u : nodes) {
            sum[c] += (long long)sz[find(u)] * sz[find(u)];
        }
        sum[c] = n * 1LL * n - sum[c];
    }

    total = 0;
    for (int c = 1; c <= n; ++c) {
        if (color_nodes.find(c) != color_nodes.end()) {
            total += sum[c];
        } else {
            total += n * 1LL * n;
        }
    }

    cout << total << '\n';

    while (m--) {
        int u, x;
        cin >> u >> x;
        update_color(u, x);
        cout << total << '\n';
    }

    return 0;
}

