using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int r, g;
    cin >> r >> g;

    // Find the maximum possible h using binary search
    long long total = r + g;
    long long low = 0, high = 2e5, h_max = 0;
    while (low <= high) {
        long long mid = (low + high) / 2;
        long long sum_mid = mid * (mid + 1) / 2;
        if (sum_mid <= total) {
            h_max = mid;
            low = mid + 1;
        } else {
            high = mid - 1;
        }
    }

    long long s = h_max * (h_max + 1) / 2;
    long long lower = max(0LL, s - r);
    long long upper = min((long long)g, s);

    if (lower > upper) {
        cout << 0 << endl;
        return 0;
    }

    // Dynamic programming to count the number of ways to achieve sum_green in [lower, upper]
    vector<int> dp(s + 1, 0);
    dp[0] = 1;

    for (int x = h_max; x >= 1; --x) {
        for (int j = s; j >= x; --j) {
            dp[j] = (dp[j] + dp[j - x]) % MOD;
        }
    }

    int ans = 0;
    for (int j = lower; j <= upper; ++j) {
        ans = (ans + dp[j]) % MOD;
    }

    cout << ans << endl;

    return 0;
}

